/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if(SAVEFLG != 'S')
		gfn_showMsg("CI00002",ds_list.RowCount()+"");
    SAVEFLG = '';    

	ufn_setGrid();
	ds_cond_copy.copy(ds_cond);
	div_pop.grd_list2.Editable = true;

	if(ds_master.GetColumn(0,"CESDDSEL") == 'S')
		ds_sill.SetColumn(0,"COL1","1");
	else if(ds_master.GetColumn(0,"CESDDSEL") == 'J')
		ds_sill.SetColumn(0,"COL2","1");
	else if(ds_master.GetColumn(0,"CESDDSEL") == 'A')
		ds_sill.SetColumn(0,"COL3","1");
	else	
	{
		ds_sill.SetColumn(0,"COL1","0");
		ds_sill.SetColumn(0,"COL2","0");
		ds_sill.SetColumn(0,"COL3","0");
	}

	if(ds_master.GetColumn(0,"CESDDGUB") > '')
	{
		div_pop.cal_dat.Enable = true;
	}
	else
	{
		div_pop.cb_gub.Index = 0;
		div_pop.cal_dat.Value = '';
		div_pop.cal_dat.Enable = false;
	}

}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(gfn_isNull(div_pop.ed_snm, true, div_pop.st_snm.Value)) return false; // 실측자
	if(gfn_isNull(div_pop.cal_hdm, true, div_pop.st_hdm.Value)) return false;	// 현장요구일
	if(gfn_isNull(div_pop.ed_jjx, true, div_pop.st_jjx.Value)) return false; // 출구유효폭
	if(ds_master.GetColumn(0,"CESDDGUB") > '')
	{
		if(gfn_isNull(div_pop.cal_dat, true, div_pop.st_dat.Value)) return false; // 재측정예정일
	}	

	ds_list.Filter("TRIM(CESDEFLR) > '' || TRIM(CESDEJHX) > '' || TRIM(CESDEJDX) > '' || TRIM(CESDEMHX) > '' || TRIM(CESDENAT) > '' || TRIM(CESDEBGO) > '' ");
	ds_list2.copyf(ds_list);
	ds_list.Filter("1=1");

	if(ds_list2.rowcount == 0)
	{
		gfn_showMsg("CW00136");
		return;
	}
	else
	{
		for( var i=0; i<ds_list2.rowcount; i++)
		{
			if(length(ds_list2.GetColumn(i,"CESDEFLR")) == 0)
			{
				gfn_showMsg("CW00001",div_pop.st_floor.value);
				div_pop.grd_list3.SetCellPos(1);
				return;
			}
		}
	}	

	for(var i=0; i<ds_list2.GetRowCount(); i++){  
		if( length(ds_list2.GetColumn(i, "CESDEFLR")) > 0 ){
			var curFlr = ds_list2.GetColumn(i, "CESDEFLR");
			var seaRow = ds_list2.SearchRow("CESDEFLR='"+curFlr+"'", 0, i-1);

			if(seaRow != -1){
				gfn_showMsg("CW00134");
				return false;
			}
		}
	}





    //=====================전기기종이 WBSS2 일경우=====================
	if( ds_master.GetColumn(0,"ZSPEC12") == 'WBSS2')
	{

		for( var j=0; j<ds_list2.rowcount; j++)
		{
			if( parseInt(ds_list2.GetColumn(j, "CESDEJDX")) < 235 )
			{			

				//gfn_showAlert("CW00022",'전기기종이 WBSS2일경우 CP 배치 불가 및 JD값은 235이상만 가능하니 ' );
				//grd_list.SetCellPos(1);
				//return;
				if(!gfn_showConfirm( "CI00024", "주의(JD값 235미만)! CP JAMB일 경우 CP배치 불가하므로 JD값을 235이상으로" +
					                            " 입력할 것, 품목표 참조. " )) return;
			}
		}	
	}

	//==================================================================		


	if(ds_sill.GetColumn(0,"COL1") == '1') {
		ds_master.SetColumn(0,"CESDDSEL","S");
	} else if(ds_sill.GetColumn(0,"COL2") == '1') {
		ds_master.SetColumn(0,"CESDDSEL","J");
	} else if(ds_sill.GetColumn(0,"COL3") == '1') {
		ds_master.SetColumn(0,"CESDDSEL","A");
	}	





	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;



	tit_clearActionInfo();		
	tit_addMultiActionInfo("ps11:PS1102003B_U1");		
	tit_addSelectExActionInfo("ps11:PS1102003B_S3", "ps11:PS1102003B_I1", "ps11:PS1102003B_U2");

	tit_callService(
		""
		, ""
		, "ds_master=ds_master ds_list=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);	
}	

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("CI00003");
		SAVEFLG = 'S';
		SELFLG = "Y";
		fn_query();
	}
}
