/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅 
********************************************************************************/
function ufn_setGrid(){
	if(ds_list.rowcount < 1) return;

	div_pop.grd_list3.Redraw = false;
	ds_list.fireEvent = false;
	var rows;
	while(80 > ds_list.GetRowCount()){
		rows = ds_list.InsertRow( ds_list.GetRowCount() );

		ds_list.SetColumn(rows,"CESDEPJT",ds_list.GetColumn(0,"CESDEPJT"));
		ds_list.SetColumn(rows,"CESDEHNO",ds_list.GetColumn(0,"CESDEHNO"));
	}
	ds_list.row = 0;
	ds_list.fireEvent = true;	
	div_pop.grd_list3.Redraw = true;
}

