		ds_sill.SetColumn(0,"COL1","1");
	else if(ds_master.GetColumn(0,"CESDDSEL") == 'J')
		ds_sill.SetColumn(0,"COL2","1");
	else if(ds_master.GetColumn(0,"CESDDSEL") == 'A')
		ds_sill.SetColumn(0,"COL3","1");

}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	ds_list.Filter("CESDEFLR > '' || CESDEJHX > '' || CESDEJDX > '' || CESDEMHX > '' || CESDENAT > '' || CESDEBGO > '' ");
	ds_list2.copyf(ds_list);
	ds_list.Filter("1=1");

	if(ds_master.rowcount == 0)
	{
		gfn_showMsg("CW00010");
		return;
	} else if (ds_list2.rowcount == 0)
	{
		gfn_showMsg("CW00136");
		return;
	}

	if (!gfn_showConfirm("CI00015",div_pop.st_data.Value)) {
		return;
	}

	tit_clearActionInfo();		
	tit_addSingleActionInfo("ps11:PS1102003B_D1");		
	tit_addSingleActionInfo("ps11:PS1102003B_D2");		
	tit_addSelectExActionInfo("ps11:PS1102003B_S4", "ps11:PS1102003B_D3", "");	

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond_copy:A"
		, ""
		, ""
		, "fn_afterDelete"
		, true);	
}

