function fn_print2() 
{
	ds_list_p.DeleteAll();
	ds_master_p.DeleteAll();
	ds_master_p.AddRow();

	// 저장할 데이터셋을 복사
	ds_master_p.CopyRow(0,"ds_master",0);
	if (ds_master_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}
	ds_master_p.SetColumn(0,"CESDDHDM",gfn_formatDate(ds_master_p.GetColumn(0,"CESDDHDM"),'.'));
	ds_master_p.SetColumn(0,"CESDDBJD",gfn_formatDate(ds_master_p.GetColumn(0,"CESDDBJD"),'.'));
	ds_master_p.SetColumn(0,"CESDDDAT",gfn_formatDate(ds_master_p.GetColumn(0,"CESDDDAT"),'.'));
	if(length(ds_master.GetColumn(0,"CESDDUPD")) > 0) {
		ds_master_p.SetColumn(0,"CESDDUPD",gfn_formatDate(substr(ds_master_p.GetColumn(0,"CESDDUPD"),0,8),'.')+" "+substr(ds_master_p.GetColumn(0,"CESDDUPD"),8,2)+":"+substr(ds_master_p.GetColumn(0,"CESDDUPD"),10,2));
	} else {
		ds_master_p.SetColumn(0,"CESDDUPD","");	
	}	
	ds_master_p.SetColumn(0,"CESDDNAP",gfn_formatDate(ds_master_p.GetColumn(0,"CESDDNAP"),'.'));
	ds_master_p.SetColumn(0,"CESDDNA2",gfn_formatDate(ds_master_p.GetColumn(0,"CESDDNA2"),'.'));
	ds_master_p.SetColumn(0,"CESDDGUB",div_pop.cb_gub.text);

	ds_master_p.SetColumn(0,"CESDDSEL","");
	ds_master_p.SetColumn(0,"CESDDSEL2","");
	ds_master_p.SetColumn(0,"CESDDSEL3","");

	if(ds_sill.GetColumn(0,"COL1") == '1') {
		ds_master_p.SetColumn(0,"CESDDSEL","V");
	} else if(ds_sill.GetColumn(0,"COL2") == '1') {
		ds_master_p.SetColumn(0,"CESDDSEL2","V");
	} else if(ds_sill.GetColumn(0,"COL3") == '1') {
		ds_master_p.SetColumn(0,"CESDDSEL3","V");
	}	

	ds_list.Filter("TRIM(CESDEFLR) > '' || TRIM(CESDEJHX) > '' || TRIM(CESDEJDX) > '' || TRIM(CESDEMHX) > '' || TRIM(CESDENAT) > '' || TRIM(CESDEBGO) > '' ");
	ds_list_p.copyf(ds_list);
	ds_list.Filter("1=1");

	for(var i = 0; i < ds_list_p.rowcount; i++)
	{
		ds_list_p.SetColumn(i,"CESDESEQ",i+1);
		if(ds_list_p.GetColumn(i,"CESDETDL") == '1') {
			ds_list_p.SetColumn(i,"CESDETDL","V");
		} else {
			ds_list_p.SetColumn(i,"CESDETDL","");	
		}	
	}

	// UbiReport Call
	Dialog("PS11::PS1102003P.xml", "");
}
