/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if(gfn_isNull(ed_pjt, true, st_pjt.value)) return;
	if(gfn_isNull(ed_hno, true, st_hno.Value)) return;

	ds_cond.SetColumn(0,"GBN","B");

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1102007A_S1");
	tit_addSearchActionInfo("ps11:PS1102007A_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
	close();
}
