/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_cond_copy.DeleteAll();
	ds_cond_copy.Copy(ds_cond);

	ufn_setGrid();

	if(ds_master.GetColumn(0,"CESDNGUB") > '')
	{
		cal_dat.Enable = true;
	}
	else
	{
		cb_gub.Index = 0;
		cal_dat.Value = '';
		cal_dat.Enable = false;
	}
	ds_master.SetColumn(0,"CESDNSNM",G_USER_NAME);
	ed_snm.SetSel();
	ed_snm.SetFocus();
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(gfn_isNull(ed_snm, true, st_snm.Value)) return false; // 실측자
	if(gfn_isNull(cal_hdm, true, st_hdm.Value)) return false;	// 현장요구일
	if(gfn_isNull(ed_jjx, true, st_jjx.Value)) return false; // 출구유효폭
	if(ds_master.GetColumn(0,"CESDNGUB") > '')
	{
		if(gfn_isNull(cal_dat, true, st_dat.Value)) return false; // 재측정예정일
	}

	ds_list.Filter("TRIM(CESDOFLR) > '' || TRIM(CESDOJHX) > '' || TRIM(CESDOJDX) > '' || TRIM(CESDOMHX) > '' || TRIM(CESDOBHX) > '' || TRIM(CESDOBWX) > '' || TRIM(CESDOBDX) > '' || TRIM(CESDONAT) > '' || TRIM(CESDOBGO) > '' ");
	ds_list2.copyf(ds_list);
	ds_list.Filter("1=1");

	if(ds_list2.rowcount == 0)
	{
		gfn_showMsg("CW00136");
		return;
	}
	else
	{
		for( var i=0; i<ds_list2.rowcount; i++)
		{
			if(length(ds_list2.GetColumn(i,"CESDOFLR")) == 0)
			{
				gfn_showMsg("CW00001",st_floor.value);
				grd_list.SetCellPos(1);
				return;
			}
		}
	}	

	for(var i=0; i<ds_list2.GetRowCount(); i++){
		if( length(ds_list2.GetColumn(i, "CESDOFLR")) > 0 ){
			var curFlr = ds_list2.GetColumn(i, "CESDOFLR");
			var seaRow = ds_list2.SearchRow("CESDOFLR='"+curFlr+"'", 0, i-1);

			if(seaRow != -1){
				gfn_showMsg("CW00134");
				return false;
			}
		}
	}
	if(!ufn_setSaveDs()) return;
	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps11:PS1102007A_S3", "ps11:PS1102007A_I1", "");
	tit_addMultiActionInfo("ps11:PS1102007A_I2");
	tit_addMultiActionInfo("ps11:PS1102007A_I3");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond_copy ds_master=ds_master1 ds_list=ds_list1"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}
/********************************************************************************
* 기      능   : dataset copy
********************************************************************************/
function ufn_setSaveDs(){
	var mrow = 0;

	ds_master1.DeleteAll();
	ds_list1.DeleteAll();

	if(length(ds_cond_copy.GetColumn(0,"HNO"))>0 && indexOf(ds_cond_copy.GetColumn(0,"HNOS"), ",") != -1){
		var hnos = Split(ds_cond_copy.GetColumn(0,"HNOS"), ",");

		for(var i=0; i<length(hnos); i++){
			if(length(hnos[i])==3){
				mrow = ds_master1.AddRow();
				ds_master1.CopyRow(mrow, ds_master, 0);			
				ds_master1.SetColumn(mrow, "CESDNHNO", hnos[i]);
				ds_master1.SetColumn(mrow, "CESDNPJT", ds_cond_copy.GetColumn(0,"PJT"));
				ds_master1.SetColumn(mrow, "GBN", "B");
				ufn_setListDs(ds_cond_copy.GetColumn(0,"PJT"), hnos[i]);
			}					
		}		
	} else { 
		mrow = ds_master1.AddRow();
		ds_master1.CopyRow(mrow, ds_master, 0);	
		ds_master1.SetColumn(mrow, "CESDNPJT", ds_cond_copy.GetColumn(0,"PJT"));
		ds_master1.SetColumn(mrow, "CESDNHNO", ds_cond_copy.GetColumn(0,"HNO"));
		ds_master1.SetColumn(mrow, "GBN", "B");

		ufn_setListDs(ds_cond_copy.GetColumn(0,"PJT"), ds_cond_copy.GetColumn(0,"HNO"));
	}

	return true;
}
/********************************************************************************
* 기      능   : dataset copy
********************************************************************************/
function ufn_setListDs(pjt, hno){
	var crow=0;
	var cseq;
	for(var j=0; j<ds_list2.GetRowCount(); j++){
		crow = ds_list1.AddRow();
		ds_list1.CopyRow(crow, ds_list2, j);
		ds_list1.SetColumn(crow, "CESDOPJT", pjt);
		ds_list1.SetColumn(crow, "CESDOHNO", hno);
		cseq = Lpad(j+1, "0", 3);
		ds_list1.SetColumn(crow, "CESDOSEQ", cseq);		
	}
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("CI00003");
	}
}
