/********************************************************************************
* 기      능   : 그리드 데이타셋값 변경시 비고란에 '품목표 참조' 세팅
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CESDOFLR":
		case "CESDOJHX":
		case "CESDOJDX":
		case "CESDOMHX":
		case "CESDOBHX":
		case "CESDOBWX":
		case "CESDOBDX":
		case "CESDONAT":
			if(length(ds_list.GetColumn(nRow,"CESDOBGO")) == 0) {
				ds_list.SetColumn(nRow,"CESDOBGO","품목표 참조");
			}	
	}
	obj.fireevent = true;
}

