/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if(gfn_isNull(ed_pjt, true, st_pjt.value)) return;
	//if(gfn_isNull(ed_hno, true, st_hno.Value)) return;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1104005A_S1");
	tit_addSearchActionInfo("ps11:PS1104005A_S2");
	tit_addSearchActionInfo("ps11:PS1104005A_S3");
	tit_addSearchActionInfo("ps11:PS1104005A_S4");
	tit_addSearchActionInfo("ps11:PS1104005A_S7");


	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_list=ds_list ds_list_all=ds_list_all ds_sum=ds_sum ds_bom=ds_bom"//ds_seq=ds_seq"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true);

//trace( ds_list.SaveXML()       );

	ds_sum_tmp.copyF(ds_sum);

    //Trace(ds_sum.saveXML());

}
/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
