							return "5000,0,1000" ;	
						}
					}	
				}

			//========================================================================
			//default:

		}
	}
	else if( gbn == 'QTY' ) 
	{
		switch( atype ) 
		{
			case "STVF":
				if(ause == 'ND' || ause == 'OH') 
				{
					if( toNumber(acapa) < 1350 ) 
					{
