				}
				else 
				{
					if( toNumber(acapa) < 1600 ) 
					{
						return "0,1,0" ;	
					}
					else 
					{
						return "1,0,0" ;	
					}
				}

			//========================================================================
			case "WBLX":
				if( toNumber(acapa) <= 600 )
				{
					return "0,1,0" ;	
				}
				else if ( toNumber(acapa) == 700 || toNumber(acapa) == 750  || toNumber(acapa) == 900 || toNumber(acapa) == 1000 )
				{
					if( toNumber(aspd) <= 105 )
					{
						return "0,1,0" ;	
					}
					else
					{
						return "1,0,0" ;	
					}
				}
				else if ( toNumber(acapa) >= 1150 )
				{
					return "1,0,0" ;	
				}				


			//========================================================================
			case "LXVF":
				if( toNumber(acapa) <= 600 )
				{
					return "0,1,0" ;	
