				if( toNumber(acapa) <= 600 )
				{
					return "0,1,0" ;	
				}
				else if ( toNumber(acapa) == 700 || toNumber(acapa) == 750  || toNumber(acapa) == 900 || toNumber(acapa) == 1000 )
				{
					if( toNumber(aspd) <= 105 )
					{
						return "0,1,0" ;	
					}
					else	// aspd > 105
					{
						return "1,0,0" ;	
					}
				}
				else if ( toNumber(acapa) >= 1150 )
				{
					return "1,0,0" ;	
				}								


			//========================================================================
			case "SSVF":				
				if( toNumber(aspd) <= 120 )
				{
					return "0,1,2" ;	
				}
				else
				{
					return "1,0,2" ;	
				}							


			//========================================================================
			case "WBSS":
				//로직변경 2018-10-30 성윤권 CJ 요청.
				/*
				if(Substr(etm,0,4) == 'GY17' || Substr(etm,0,4) == 'GY20')
				{
					return "0,1,0" ;
				}
				else if(Substr(etm,0,4) == 'GY25' || Substr(etm,0,4) == 'GY35')
				{
					if(toNumber(astq) <= 10)
					{
						return "0,1,0";
					}
					else
					{
						return "0,1,2";
					}
				}
				else if(Substr(etm,0,4) == 'GY40' || Substr(etm,0,4) == 'GY55')
				{
					return "1,0,2";
				}
				*/

				if(ause == 'ND' || ause == 'OH') 
				{
					if( toNumber(acapa) <= 700 ) 
					{
						return "0,1,0";	
					}
					else if( toNumber(acapa) == 750  )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return "0,1,0";	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return "0,1,2";	
							}
							else
							{
								return "1,0,2";	
							}

						}
					}
					else if ( toNumber(acapa) >= 900 ) 
					{
						if( toNumber(astq) <= 10 ) 
						{
							return "1,0,2";	
						}
						else
						{
							return "1,0,2";	
						}
					}						
				}
				else 
				{
					if( toNumber(acapa) <= 900 ) 
					{
						return "0,1,0" ;	
					}
					else if( toNumber(acapa) == 1000  )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return "0,1,0" ;	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return "0,1,2" ;	
							}
							else
							{
								return "1,0,2" ;	
							}

						}
					}
					else if ( toNumber(acapa) >= 1150 ) 
					{
						if( toNumber(astq) <= 10 ) 
						{
							return "1,0,2" ;	
						}
						else
						{
							return "1,0,2" ;	
						}
					}	
				}

			//========================================================================
			case "GTSS":			
				if(ause == 'ND' || ause == 'OH') 
				{

				}
				else 
				{
					if( toNumber(acapa) <= 900 ) 
					{
						return "0,1,0" ;	
					}
					else if( toNumber(acapa) == 1000 )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return "0,1,0" ;	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return "0,1,2" ;	
							}
							else
							{
								return "1,0,2" ;	
							}

						}
					}
					else if ( toNumber(acapa) >= 1150 ) 
					{
						if( toNumber(astq) <= 10 ) 
						{
							return "1,0,2" ;	
						}
						else
						{
							return "1,0,2" ;	
						}
					}	
				}
			//========================================================================
			//default:

		}

	}

}


/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

     if(gfn_isNull(ed_pjt, true, st_pjt.value)) return false;  //pjt
     if(ds_sum.RowCount() <= 0 ){ // 신청수량 
        gfn_showMsg("CW00153");
		return;                 }

   	 if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}
    //소요시기
	 if(length(ds_master.GetColumn(0,"USE_DAT")) <= 0 )
	{
		      gfn_showMsg("CI30001","소요시기를 입력하세요.");
			  return;
	}

	//소요시기 현재일 +3일 이후로 지정 가능.
	var sNextDate = gfn_getAfterDate(gfn_getCurrDate(),3);	// 현재일 + 3일

	if(ds_master.GetColumn(0, "USE_DAT") < sNextDate)
	{
		gfn_openMsgWin("[소요시기] 신청일 기준, 최소 3일 이후로\n날짜를 지정하십시오.\n(당일출고 불가)");
		cal_usedat.SetFocus();
		return false;
	}


	// 현장 담당자 + TEL
	for(var i=0; i<ds_list.GetRowCount(); i++){ 

		if(ds_list.GetColumn(i, "CHK")==1){

		   if(length(ds_list.GetColumn(i,"SITE_TEL")) <= 0 )
		   {
		      gfn_showMsg("CI30001","현장담당자 전화번호를 입력하세요.");
			  return;
		   }
		    if(length(ds_list.GetColumn(i,"SITE_MAN")) <= 0 )
		   {
		      gfn_showMsg("CI30001","현장담당자를 입력하세요.");
			  return;
		   }
		   if(ds_list.GetColumn(i,"CAPA1") == 0  && ds_list.GetColumn(i,"CAPA2") == 0 && ds_list.GetColumn(i,"CAPA3") == 0)
		   { 
		      gfn_showMsg("CI30001","수량이 없는 현장은 신청할 수 없습니다.");
			  return;
		   }
		   if(ds_list.GetColumn(i,"QTY1") == 0  && ds_list.GetColumn(i,"QTY2") == 0 && ds_list.GetColumn(i,"QTY3") == 0)
		   { 
		      gfn_showMsg("CI30001","수량이 없는 현장은 신청할 수 없습니다.");
			  return;
		   }

		}
     }

	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	tit_clearActionInfo();
    tit_addSearchActionInfo("ps11:PS1104005A_S5");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_seq=ds_seq"
        , ""
        , "fn_aftersave"
        , ""
        , ""
        , true);

}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_aftersave() {

	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

   	ds_master.SetColumn(0,"CHGNO", ds_seq.GetColumn(0,"CHGNO"));

	for(var i=0; i < ds_list2.rowcount; i++)
     {
        ds_list2.SetColumn(i,"CHGNO", ds_seq.GetColumn(0,"CHGNO"));
     }			    

    //trace(ds_list2.SaveXML());

     tit_clearActionInfo();
	 tit_addSingleActionInfo("ps11:PS1104005A_I1");
	 tit_addMultiActionInfo("ps11:PS1104005A_I2");
	 tit_addMultiActionInfo("ps11:PS1104005A_I4");
	// tit_addMultiActionInfo("ps11:PS1104005A_I3");

    	tit_callService(
		""
		, ""
		, "ds_master=ds_master ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave2"
		,""
		,""
		, true);



}
