/********************************************************************************
* 기      능   : 그리드 데이타셋값 변경시 비고란에 '품목표 참조' 세팅
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	obj.fireevent = false;

	switch( strColumnID ) 
	{
		case "CHK":		
			 if(length(ds_list.GetColumn(nRow, "TYPE")) == 0)
			 {			
				ds_list.SetColumn(nRow, "CHK", " ");
				gfn_showAlert("CI30001", "재질값이 없으면 신청이 불가능 합니다.");			 	
				obj.fireevent = true;
				return;
			 }
			 else
			 {
				ufn_ds_sum();
			 }

			break;
	}

	obj.fireevent = true;
}

/********************************************************************************
* 기      능   : ds_list 체크시 ds_sum에 type, capa 별 수량 sum
********************************************************************************/
function ufn_ds_sum()
{


	var lv_qty = 0;
	var lv_all_qty = 0;

	for(var i=0; i<ds_list.GetRowCount(); i++) 
	{
		if( ds_list.GetColumn(i, "CHK") == 1 )
		{
			ds_list_tmp.addRow();
			ds_list_tmp.CopyRow(ds_list_tmp.row, "ds_list", i);
		}
	}
	//Trace(ds_list_tmp.saveXML());

	ds_sum.ClearData();
	ds_sum.copyF(ds_sum_tmp);

	for(var i=0; i<ds_list_tmp.GetRowCount(); i++)
	{

		if(  ds_list_tmp.GetColumn(i, "CAPA1")  > 0 )
		{

			if( ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID1") )  == -1 )
			{		
				ds_sum.AppendRow();				
				ds_sum.SetColumn(ds_sum.row,"PSPID", ds_list_tmp.GetColumn(i, "PSPID") );
				ds_sum.SetColumn(ds_sum.row,"TYPE", ds_list_tmp.GetColumn(i, "TYPE") );			
				ds_sum.SetColumn(ds_sum.row,"CAPA", ds_list_tmp.GetColumn(i, "CAPA1") );
				ds_sum.SetColumn(ds_sum.row,"QTY", toNumber(ds_list_tmp.GetColumn(i, "QTY1")) );		

				ds_sum.SetColumn(ds_sum.row,"ALL_QTY", toNumber(ds_list_tmp.GetColumn(i, "QTY1")) );  //==> 수정						
				ds_sum.SetColumn(ds_sum.row,"POS_QTY", 0 );
				ds_sum.SetColumn(ds_sum.row,"SEARCHID", ds_sum.GetColumn(ds_sum.row,"TYPE") + ds_sum.GetColumn(ds_sum.row,"CAPA") ); 
			}
			else
			{

				lv_qty = 0;
				lv_qty = ds_sum.GetColumn( ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID1") ) , "QTY") ;				
				lv_qty = toNumber(lv_qty) + toNumber(ds_list_tmp.GetColumn(i, "QTY1"));							
				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID1")),"QTY", lv_qty);					
				lv_all_qty = ds_sum_tmp.GetColumn( ds_sum_tmp.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID1")), "ALL_QTY");  //==> 추가	
				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID1")),"ALL_QTY", lv_qty +  toNumber(lv_all_qty));  //==> 추가	
			}
		}


		if( ds_list_tmp.GetColumn(i, "CAPA2") > 0 )
		{

			if( ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID2") )  == -1 )
			{

				ds_sum.AppendRow();				
				ds_sum.SetColumn(ds_sum.row,"PSPID", ds_list_tmp.GetColumn(i, "PSPID") );
				ds_sum.SetColumn(ds_sum.row,"TYPE", ds_list_tmp.GetColumn(i, "TYPE") );			
				ds_sum.SetColumn(ds_sum.row,"CAPA", ds_list_tmp.GetColumn(i, "CAPA2") );
				ds_sum.SetColumn(ds_sum.row,"QTY", toNumber(ds_list_tmp.GetColumn(i, "QTY2")) );												
				ds_sum.SetColumn(ds_sum.row,"ALL_QTY", toNumber(ds_list_tmp.GetColumn(i, "QTY2")) );  //==> 수정	
				ds_sum.SetColumn(ds_sum.row,"POS_QTY", 0 );
				ds_sum.SetColumn(ds_sum.row,"SEARCHID", ds_sum.GetColumn(ds_sum.row,"TYPE") + ds_sum.GetColumn(ds_sum.row,"CAPA") );
			}
			else
			{

				lv_qty = 0;
				lv_qty = ds_sum.GetColumn( ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID2") ) , "QTY") ;	
				lv_qty = toNumber(lv_qty) + toNumber(ds_list_tmp.GetColumn(i, "QTY2"));			
				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID2")),"QTY", lv_qty);
				lv_all_qty = ds_sum_tmp.GetColumn( ds_sum_tmp.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID2")), "ALL_QTY");  //==> 추가	
				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID2")),"ALL_QTY", lv_qty + toNumber(lv_all_qty));  //==> 추가	
			}
		}	


		if( ds_list_tmp.GetColumn(i, "CAPA3") > 0 )
		{

			if( ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID3") )  == -1 )
			{
				ds_sum.AppendRow();				
				ds_sum.SetColumn(ds_sum.row,"PSPID", ds_list_tmp.GetColumn(i, "PSPID") );
				ds_sum.SetColumn(ds_sum.row,"TYPE", ds_list_tmp.GetColumn(i, "TYPE") );			
				ds_sum.SetColumn(ds_sum.row,"CAPA", ds_list_tmp.GetColumn(i, "CAPA3") );
				ds_sum.SetColumn(ds_sum.row,"QTY", toNumber(ds_list_tmp.GetColumn(i, "QTY3")) );																		
				ds_sum.SetColumn(ds_sum.row,"ALL_QTY", toNumber(ds_list_tmp.GetColumn(i, "QTY3")) );  //==> 수정	
				ds_sum.SetColumn(ds_sum.row,"POS_QTY", 0 );
				ds_sum.SetColumn(ds_sum.row,"SEARCHID", ds_sum.GetColumn(ds_sum.row,"TYPE") + ds_sum.GetColumn(ds_sum.row,"CAPA") );
			}
			else
			{

				lv_qty = 0;
				lv_qty = ds_sum.GetColumn( ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID3") ) , "QTY") ;	
				lv_qty = toNumber(lv_qty) + toNumber(ds_list_tmp.GetColumn(i, "QTY3"));			
				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID3")),"QTY", lv_qty);
				lv_all_qty = ds_sum_tmp.GetColumn( ds_sum_tmp.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID3")), "ALL_QTY");  //==> 추가	
				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID3")),"ALL_QTY", lv_qty + toNumber(lv_all_qty));  //==> 추가	
			}
		}		    



	}
	//Trace(ds_sum.saveXML());	
	ds_list_tmp.ClearData();

}


/********************************************************************************
* 기      능   : ds_sum  의  ALL_QTY sum
********************************************************************************/
function ds_sum_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	var lv_all_qty = 0;
	grd_list3.ReDraw = false;
	obj.fireevent = false;
	switch( strColumnID ) 
	{
		case "QTY":
			lv_all_qty = ds_sum_tmp.GetColumn( ds_sum_tmp.FindRow("SEARCHID",  ds_sum.GetColumn(nRow, "SEARCHID")), "ALL_QTY" );			             		
			obj.SetColumn(nRow, "ALL_QTY", (obj.GetColumn(nRow, strColumnID) + lv_all_qty ));
			break;

	}	

	obj.fireevent = true;
	grd_list3.ReDraw = true;
}








