function ufn_getUrl() {
	var url = 'http://203.242.40.61:55002/MobileServer/map/index.jsp?m=';
	if (IndexOf(G_SERVER_URL, 'localhost') > 0 || IndexOf(G_SERVER_URL, '203.242.37.24') > 0) {
		url = 'http://203.242.40.209:8090/MobileServer/map/index.jsp?m=';
	}
	url += ds_master.getColumn(0, "POSID");
	return url;
}

/********************************************************************************
* 기      능   : 지도보기
********************************************************************************/
function btnGoUrl_OnClick(obj)
{
	if (Length(Trim(ds_master.getColumn(0, "POSID"))) == 0) {
		gfn_showAlert("CI30001", "프로젝트를 조회하십시오.");
		return;
	}
	if (Length(Trim(ds_master.getColumn(0, "G_ADDR"))) == 0) {
		gfn_showAlert("CI30001", "설정된 구글맵이 없습니다.");
		return;
	}

	ExecBrowser(ufn_getUrl());
}

/********************************************************************************
* 기      능   : 최신현장전화 수정
********************************************************************************/
function btn_modify_OnClick(obj)
{
	var sPosId = ds_master.getColumn(0, "PSPID");

	if(sPosId == "" || length(sPosId) == 0) return;

	//# 현장 전화번호 저장
	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps01:PS0101001A_S9", "ps01:PS0101001A_I1", "ps01:PS0101001A_U1");	// ZPSTPM07 INSERT OR UPDATE.
	tit_callService(
		""
		, ""
		, "ds_head=ds_master"
		, ""
		, ""
		, "fn_SaveTel"
		, ""
		, ""
		, true  // ""

		);
}

/********************************************************************************
* 기      능   : 최신현장전화 수정 후 Callback
********************************************************************************/
function fn_SaveTel()
{
	if ( errCode!= 0 ) {
	//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");'
		gfn_showMsg("CE00001");
	} else {
		//gfn_showMsg("CI00003");
		gfn_openMsgWin("최신 현장전화번호가 수정되었습니다.");
		fn_query();

	}
}

function Edit20_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	// 숫자와 - 만 입력가능하도록 설정
	// 8 : backspace 키, 46 : Delete 키, 45 : -, 나머지 : 숫자 키
	if(!(nChar>= 48 && nChar <= 57) && nChar != 45 && nChar != 46 && nChar != 8)
	{
		return false;
	}
}

