/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery() {

	var atype = '';
	var ause = '';
	var aman = '';
	var aspd = '';
	var astq = '';
	var gbn = '';
	var capa = '';
	var qty = '';

	ds_list.fireevent = false;

	for( var i=0; i<ds_list.rowcount; i++)
	{
		atype = ds_list.GetColumn(i, "EL_ATYP");
		ause  = ds_list.GetColumn(i, "EL_AUSE");
		aman  = ds_list.GetColumn(i, "EL_AMAN");
		aspd  = ds_list.GetColumn(i, "EL_ASPD");
		astq  = ds_list.GetColumn(i, "EL_ASTQ");


		gbn   = 'CAPA';  // function call 할때 중량과 수량을 구분하기 위함
		capa  = Split(ufn_atnam(atype, ause, aman, aspd, astq, gbn) , ",");				
		for(var j=0; j<length(capa); j++)
		{
			//if( length(trim(capa[j])) > 0 )
			if( j == 0 )
			{
				ds_list.SetColumn(i, "CAPA1", trim(capa[j]) );
			}
			if( j == 1 )
			{
				ds_list.SetColumn(i, "CAPA2", trim(capa[j]) );
			}
			if( j == 2 )
			{
				ds_list.SetColumn(i, "CAPA3", trim(capa[j]) );
			}			    
		}


		gbn   = 'QTY';
		qty   = Split(ufn_atnam(atype, ause, aman, aspd, astq, gbn) , ",");	
				for(var j=0; j<length(qty); j++)
		{
			//if( length(trim(capa[j])) > 0 )
			if( j == 0 )
			{
				ds_list.SetColumn(i, "QTY1", trim(qty[j]) );
			}
			if( j == 1 )
			{
				ds_list.SetColumn(i, "QTY2", trim(qty[j]) );
			}
			if( j == 2 )
			{
				ds_list.SetColumn(i, "QTY3", trim(qty[j]) );
			}			    
		}

		ds_list.SetColumn(i, "SEARCHID1", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA1")));
		ds_list.SetColumn(i, "SEARCHID2", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA2")));
		ds_list.SetColumn(i, "SEARCHID3", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA3")));


	}
	ds_list.fireevent = true;
}



/********************************************************************************
* 기      능   : function : HOOK  수량 및 CAPA 가져오기
********************************************************************************/
function ufn_atnam(atype, ause, aman, aspd, astq, gbn) {

	if ( gbn == 'CAPA' ) 
	{
		switch( atype ) 
		{
			case "STVF":
				if(ause == 'ND' || ause == 'OH') 
				{
					if( toNumber(aman) < 20 ) 
					{
						return "0,3000,0" ;	
					}				
					else 
					{
						return "5000,0,0" ;	
					}						
				}
				else 
				{
					if( toNumber(aman) < 24 ) 
					{
						return "0,3000,0" ;	
					}
					else 
					{
						return "5000,0,0" ;	
					}
				}

			//========================================================================
			case "LXVF":
				if( toNumber(aman) <= 9 )
				{
					return " 0,3000,0" ;	
				}
				else if ( toNumber(aman) = 10 || toNumber(aman) = 11  || toNumber(aman) = 13 || toNumber(aman) = 15 )
				{
					if( toNumber(aspd) <= 105 )
					{
						return "0,3000,0" ;	
					}
					else
					{
						return "5000,0,0" ;	
					}
				}
				else if ( toNumber(aman) >= 17 )
				{
					return "5000,0,0" ;	
				}				


			//========================================================================
			case "SSVF":
				if( toNumber(aman) <= 11 )
				{
					return "0,3000,1000" ;	
				}
				else if ( toNumber(aman) = 13 || toNumber(aman) = 15 || toNumber(aman) = 17 )
				{
					if( toNumber(aspd) <= 120 )
					{
						return "0,3000,1000" ;	
					}
					else
					{
						return "5000,0,1000" ;	
					}
				}
				else if ( toNumber(aman) >= 20 )
				{
					return "5000,0,1000" ;	
				}							


			//========================================================================
			case "WBSS":
				if(ause == 'ND' || ause == 'OH') 
				{
					if( toNumber(aman) <= 10 ) 
					{
						return " 0,2000,0" ;	
					}
					else if( toNumber(aman) = 11  )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return "0,2000,0" ;	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return " 0,2000,0" ;	
							}
							else
							{
								return "5000,0,1000" ;	
							}

						}
					}
					else if ( toNumber(aman) >= 13 ) 
					{
						if( toNumber(astq) <= 10 ) 
						{
							return "5000,0,0 " ;	
						}
						else
						{
							return "5000,0,1000" ;	
						}
					}						
				}
				else 
				{
					if( toNumber(aman) <= 13 ) 
					{
						return " 0,2000,0 " ;	
					}
					else if( toNumber(aman) = 15  )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return " 0,2000,0 " ;	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return "0,2000,0" ;	
							}
							else
							{
								return "5000,0,1000" ;	
							}

						}
					}
					else if ( toNumber(aman) >= 17 ) 
					{
						if( toNumber(astq) <= 10 ) 
						{
							return "5000,0,0 " ;	
						}
						else
						{
							return "5000,0,1000" ;	
						}
					}	
				}
			//========================================================================
			//default:

		}
	}
	else if( gbn == 'QTY' ) 
	{
		switch( atype ) 
		{
			case "STVF":
				if(ause == 'ND' || ause == 'OH') 
				{
					if( toNumber(aman) < 20 ) 
					{
						return " 0,1,0" ;	
					}				
					else 
					{
						return "1,0,0" ;	
					}						
				}
				else 
				{
					if( toNumber(aman) < 24 ) 
					{
						return "0,1,0 " ;	
					}
					else 
					{
						return "1,0,0 " ;	
					}
				}

			//========================================================================
			case "LXVF":
				if( toNumber(aman) <= 9 )
				{
					return "0,1,0 " ;	
				}
				else if ( toNumber(aman) = 10 || toNumber(aman) = 11  || toNumber(aman) = 13 || toNumber(aman) = 15 )
				{
					if( toNumber(aspd) <= 105 )
					{
						return " 0,1,0" ;	
					}
					else
					{
						return "1,0,0" ;	
					}
				}
				else if ( toNumber(aman) >= 17 )
				{
					return "1,0,0" ;	
				}				


			//========================================================================
			case "SSVF":
				if( toNumber(aman) <= 11 )
				{
					return "0,1,2" ;	
				}
				else if ( toNumber(aman) = 13 || toNumber(aman) = 15 || toNumber(aman) = 17 )
				{
					if( toNumber(aspd) <= 120 )
					{
						return " 0,1,2" ;	
					}
					else
					{
						return "1,0,2" ;	
					}
				}
				else if ( toNumber(aman) >= 20 )
				{
					return "1,0,2" ;	
				}							


			//========================================================================
			case "WBSS":
				if(ause == 'ND' || ause == 'OH') 
				{
					if( toNumber(aman) <= 10 ) 
					{
						return " 0,1,0" ;	
					}
					else if( toNumber(aman) = 11  )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return " 0,1,0" ;	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return " 0,1,0" ;	
							}
							else
							{
								return "1,0,2" ;	
							}

						}
					}
					else if ( toNumber(aman) >= 13 ) 
					{
						if( toNumber(astq) <= 10 ) 
						{
							return "1,0,0 " ;	
						}
						else
						{
							return "1,0,2" ;	
						}
					}						
				}
				else 
				{
					if( toNumber(aman) <= 13 ) 
					{
						return "0,1,0" ;	
					}
					else if( toNumber(aman) = 15  )
					{
						if( toNumber(aspd) <= 60 ) 
						{
							return " 0,1,0 " ;	
						}
						else
						{
							if( toNumber(astq) <= 10 ) 
							{
								return "0,1,0" ;	
