/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

     if(gfn_isNull(ed_pjt, true, st_pjt.value)) return false;  //pjt
     if(ds_sum.RowCount() <= 0 ){ // 신청수량 
        gfn_showMsg("CW00153");
		return;                 }

   	 if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}
    //소요시기
	 if(length(ds_master.GetColumn(0,"USE_DAT")) <= 0 )
		   {
		      gfn_showMsg("CI30001","소요시기를 입력하세요.");
			  return;
		   }
	// 현장 담당자 + TEL
	for(var i=0; i<ds_list.GetRowCount(); i++){ 

		if(ds_list.GetColumn(i, "CHK")==1){

		   if(length(ds_list.GetColumn(i,"SITE_TEL")) <= 0 )
		   {
		      gfn_showMsg("CI30001","현장담당자 전화번호를 입력하세요.");
			  return;
		   }
		    if(length(ds_list.GetColumn(i,"SITE_MAN")) <= 0 )
		   {
		      gfn_showMsg("CI30001","현장담당자를 입력하세요.");
			  return;
		   }
		   if(ds_list.GetColumn(i,"CAPA1") == 0  && ds_list.GetColumn(i,"CAPA2") == 0 && ds_list.GetColumn(i,"CAPA3") == 0)
		   { 
		      gfn_showMsg("CI30001","수량이 없는 현장은 신청할 수 없습니다.");
			  return;
		   }

		}
     }

	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	tit_clearActionInfo();
    tit_addSearchActionInfo("ps11:PS1104005A_S5");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_seq=ds_seq"
        , ""
        , "fn_aftersave"
        , ""
        , ""
        , true);

}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_aftersave() {

	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

   	ds_master.SetColumn(0,"CHGNO", ds_seq.GetColumn(0,"CHGNO"));

	for(var i=0; i < ds_list2.rowcount; i++)
     {
        ds_list2.SetColumn(i,"CHGNO", ds_seq.GetColumn(0,"CHGNO"));
     }			    

    //trace(ds_list2.SaveXML());

     tit_clearActionInfo();
	 tit_addSingleActionInfo("ps11:PS1104005A_I1");
	 tit_addMultiActionInfo("ps11:PS1104005A_I2");
	 tit_addMultiActionInfo("ps11:PS1104005A_I4");
	// tit_addMultiActionInfo("ps11:PS1104005A_I3");

    	tit_callService(
		""
		, ""
		, "ds_master=ds_master ds_list2=ds_list2"
		, ""
		, ""
		, //"""fn_afterSave2"
		, true);


	tit_clearActionInfo();	

	tit_addSingleActionInfo("ps11:PS1104005A_D1");
    tit_addMultiActionInfo("ps11:PS1104005A_I3");
	tit_callService(
        ""
        , ""        
        , "ds_sum=ds_sum"
        , ""
        , ""
        , "fn_afterSave2"
		, ""
		, ""
        ,true  // ""
        );	
}
