/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1104006C_S1");
    tit_addSearchActionInfo("ps11:PS1104006B_S1");
	tit_addSearchActionInfo("ps11:PS1104006B_S2");
	//tit_addSearchActionInfo("ps11:PS1104006B_S3");

	tit_addSearchActionInfo("ps11:PS1104006C_S2");
	tit_addSearchActionInfo("ps11:PS1104005A_S7");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_list=ds_list ds_list_all=ds_list_all ds_sum=ds_sum ds_bom=ds_bom"//ds_list_tmp=ds_list_tmp"//ds_seq=ds_seq"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true);



}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save(){

     if(ds_sum.RowCount() <= 0 ){ // 신청수량 
        gfn_showMsg("CW00153");
		return;                 }

   	 if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}
	// 현장 담당자 + TEL
	for(var i=0; i<ds_list.GetRowCount(); i++){ 
		if(ds_list.GetColumn(i, "CHK")==1){
		   if(length(ds_list.GetColumn(i,"SITE_TEL")) <= 0 )
		   {
		      gfn_showMsg("CI30001","현장담당자 전화번호를 입력하세요.");
			  return;
		   }
		    if(length(ds_list.GetColumn(i,"SITE_MAN")) <= 0 )
		   {
		      gfn_showMsg("CI30001","현장담당자를 입력하세요.");
			  return;
		   }
		}
     }

	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();


	for(var i=0; i < ds_list2.rowcount; i++)
     {
        ds_list2.SetColumn(i,"REQ_DAT", ds_master.GetColumn(0,"REQ_DAT"));
        ds_list2.SetColumn(i,"CHGNO", ds_master.GetColumn(0,"CHGNO"));
     }			    


    tit_clearActionInfo();	
	tit_addMultiActionInfo("ps11:PS1104006B_U1");	// ZPSTHOOKM UPDATE

	//tit_addMultiActionInfo("ps11:PS1104005A_D1");  // ZPSTHOOKS DELETE
    tit_addMultiActionInfo("ps11:PS1104005A_I3");   // ZPSTHOOKS INSERT 

	tit_addMultiActionInfo("ps11:PS1104006B_D1");  // ZPSTHOOKD DELETE
	tit_addMultiActionInfo("ps11:PS1104006B_I1");   // ZPSTHOOKD INSERT
	tit_callService(
        ""
        , ""        
        , "ds_master=ds_master ds_sum=ds_sum ds_list2=ds_list2"
        , ""
        , ""
        , "fn_afterSave"
		, ""
		, ""
        ,true  // ""
        );	

}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	//parent.fn_query();
	close();
	//fn_query();
}

