// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {	

	if(ds_list.GetRowCount() > 0)
	{
		tit_callService(
        "PS1104005A_ACT"
        , "query"
        , "ds_list=ds_list"
        , "ds_list_tmp2=ds_list_tmp"
        , ""
        , "fn_afterQuery2"
        , false); 
	}


}

function fn_afterQuery2()
{
	var nFindRow = 0;

	ds_list.fireevent = false;

	for(var i=0;i<ds_list.getRowCount();i++)
	{
		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_DHK'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "TYPE", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_ATYP'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_ATYP", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_AUSE'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_AUSE", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_AMAN'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_AMAN", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_ASPD'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_ASPD", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_ASTQ'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_ASTQ", ds_list_tmp2.GetColumn(nFindRow, "ATFLY"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_ETM'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_ETM", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}


		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_ACAPA'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_ACAPA", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}

		nFindRow = ds_list_tmp2.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"' && ATNAM == 'EL_ABANK'");

		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "EL_ABANK", ds_list_tmp2.GetColumn(nFindRow, "ATWRT"));
		}
	}

	ds_list.fireevent = true;

	var atype = '';
	var ause = '';
	var aman = '';
	var aspd = '';
	var astq = '';
	var gbn = '';
	var capa = '';
	var qty = '';
	var etm = '';
	var acapa = '';
	var abank = '';

	ds_list.fireevent = false;

	for( var i=0; i<ds_list.rowcount; i++)
	{
		if(Substr(ds_list.GetColumn(i, "PSPID"),0,1) != 'N') //교체공사(N)가 아닌 경우
		{
			atype = ds_list.GetColumn(i, "EL_ATYP");
			ause  = ds_list.GetColumn(i, "EL_AUSE");
			aman  = ds_list.GetColumn(i, "EL_AMAN");
			aspd  = ds_list.GetColumn(i, "EL_ASPD");
			astq  = ds_list.GetColumn(i, "EL_ASTQ");
			etm   = ds_list.GetColumn(i, "EL_ETM");
			acapa   = ds_list.GetColumn(i, "EL_ACAPA");
			abank   = ds_list.GetColumn(i, "EL_ABANK");

			gbn   = 'CAPA';  // function call 할때 중량과 수량을 구분하기 위함
			capa  = Split(ufn_atnam(atype, ause, aman, aspd, astq, etm, gbn, acapa, abank) , ",");				
			for(var j=0; j<length(capa); j++)
			{
				//if( length(trim(capa[j])) > 0 )
				if( j == 0 )
				{
					ds_list.SetColumn(i, "CAPA1", trim(capa[j]) );
				}
				if( j == 1 )
				{
					ds_list.SetColumn(i, "CAPA2", trim(capa[j]) );
				}
				if( j == 2 )
				{
					ds_list.SetColumn(i, "CAPA3", trim(capa[j]) );
				}			    
			}


			gbn   = 'QTY';
			qty   = Split(ufn_atnam(atype, ause, aman, aspd, astq, etm, gbn, acapa, abank) , ",");	
					for(var j=0; j<length(qty); j++)
			{
				//if( length(trim(capa[j])) > 0 )
				if( j == 0 )
				{
					ds_list.SetColumn(i, "QTY1", trim(qty[j]) );
				}
				if( j == 1 )
				{
					ds_list.SetColumn(i, "QTY2", trim(qty[j]) );
				}
				if( j == 2 )
				{
					ds_list.SetColumn(i, "QTY3", trim(qty[j]) );
				}			    
			}

			ds_list.SetColumn(i, "SEARCHID1", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA1")));
			ds_list.SetColumn(i, "SEARCHID2", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA2")));
			ds_list.SetColumn(i, "SEARCHID3", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA3")));
		}
		else
		{

			// CAPA
			ds_list.SetColumn(i, "CAPA1", 0);
			ds_list.SetColumn(i, "CAPA2", 3000);
			ds_list.SetColumn(i, "CAPA3", 0);

			// 수량
			var nSearchRow = ds_bom.SearchRow("HOGIN == '"+ds_list.GetColumn(i, "POSID")+"'");

			if(nSearchRow>= 0)
			{
				ds_list.SetColumn(i, "QTY1", 0);
				ds_list.SetColumn(i, "QTY2", ds_bom.GetColumn(nSearchRow, "MENGE"));
				ds_list.SetColumn(i, "QTY3", 0);
			}
			else
			{
				ds_list.SetColumn(i, "QTY1", 0);
				ds_list.SetColumn(i, "QTY2", 0);
				ds_list.SetColumn(i, "QTY3", 0);
			}

			ds_list.SetColumn(i, "SEARCHID1", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA1")));
			ds_list.SetColumn(i, "SEARCHID2", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA2")));
			ds_list.SetColumn(i, "SEARCHID3", trim(ds_list.GetColumn(i, "TYPE")) + trim(ds_list.GetColumn(i, "CAPA3")));
		}

	}

	ds_list.fireevent = true;

    //ufn_ds_sum();

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode > 0 ) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("CI00003");
		parent.fn_query();
	}

	//fn_query();
}


