				ds_sum.SetColumn(ds_sum.FindRow("SEARCHID", ds_list_tmp.GetColumn(i, "SEARCHID3")),"ALL_QTY", lv_qty + toNumber(lv_all_qty));  //==> 추가	
			}
		}		    



	}

	ds_list_tmp.ClearData();

}


/********************************************************************************
* 기      능   : ds_sum  의  ALL_QTY sum
********************************************************************************/
function ds_sum_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
//alert("a");
	var lv_all_qty = 0;
	grd_list3.ReDraw = false;
	obj.fireevent = false;
	switch( strColumnID ) 
	{
		case "QTY":
			lv_all_qty = ds_sum_tmp.GetColumn( ds_sum_tmp.FindRow("SEARCHID",  ds_sum.GetColumn(nRow, "SEARCHID")), "ALL_QTY" );			             		
			obj.SetColumn(nRow, "ALL_QTY", (obj.GetColumn(nRow, strColumnID) + lv_all_qty ));
			break;

	}	

	obj.fireevent = true;
	grd_list3.ReDraw = true;


}



function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;

	switch( strColumnID ) 
	{
		case "CHK":		

			if(length(ds_list.GetColumn(nRow, "TYPE")) == 0)
			 {			
			 	if(ds_list.GetColumn(nRow, "CHK") == "1")
			 	{
					ds_list.SetColumn(nRow, "CHK", " ");
					gfn_showAlert("CI30001", "재질값이 없으면 신청이 불가능 합니다.");
			 	}
			 	else
			 	{
					ds_list.SetColumn(nRow, "CHK", "1");
					gfn_showAlert("CI30001", "재질값이 없는 기존 신청데이터는 수정이 불가능 합니다.");				
			 	}

				obj.fireevent = true;
				return;
			 }
			 else
			 {
				ufn_ds_sum();
			 }
			break;
	}

	obj.fireevent = true;
}


/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
if(!gfn_showConfirm( "CI00015", "해당 데이터" )) return;

	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps11:PS1104005A_S6");  

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_sum2=ds_sum2"
		, ""
		, "fn_afterDelete"
		//, ""
		//, ""
		, true);

}

/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_afterDelete() {

	tit_clearActionInfo();		
	tit_addSingleActionInfo("ps11:PS1104006B_D4");  // MASTER
	tit_addSingleActionInfo("ps11:PS1104006B_D2");   //DETAIL 
	tit_addMultiActionInfo("ps11:PS1104006B_U2"); // TOTAL

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond ds_sum2=ds_sum2"
		, ""
		, ""
		, "fn_afterDelete2"
		//, ""
		//, ""
		, true);

}


/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete2(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
		parent.fn_query();    
    	fn_close();
	}	

}

/********************************************************************************
* 기      능   : (-)버튼 클릭시
********************************************************************************/


function ufn_getUrl() {
	var url = 'http://203.242.40.61:55002/MobileServer/map/index.jsp?m=';
	if (IndexOf(G_SERVER_URL, 'localhost') > 0 || IndexOf(G_SERVER_URL, '203.242.37.24') > 0) {
		url = 'http://203.242.40.209:8090/MobileServer/map/index.jsp?m=';
	}
	url += ds_master.getColumn(0, "POSID");
	return url;
}

/********************************************************************************
* 기      능   : 지도보기
********************************************************************************/
function btnGoUrl_OnClick(obj)
{
	if (Length(Trim(ds_master.getColumn(0, "POSID"))) == 0) {
		gfn_showAlert("CI30001", "프로젝트를 조회하십시오.");
		return;
	}
	if (Length(Trim(ds_master.getColumn(0, "G_ADDR"))) == 0) {
		gfn_showAlert("CI30001", "설정된 구글맵이 없습니다.");
		return;
	}

	ExecBrowser(ufn_getUrl());
}


