function fn_query() {
	if(gfn_isNull(ed_pjt, true, Static0.Text)) return;
	if(gfn_isNull(ed_hno, true, Static2.Text)) return;
	ds_detail.DeleteAll();

	POSID = ed_pjt.Text + ed_hno.Text;
	ds_cond.SetColumn(0, "POSID", POSID);

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps11:PS1101003A_S1");
	tit_addSearchActionInfo("ps11:PS1107001A_S1");
	tit_addSearchActionInfo("ps11:PS1107001A_S2");


	// 조회 화면
	// tit_addSearchActionInfo("ps11:PS1101003B_S1");
	// tit_addSearchActionInfo("ps11:PS1101003B_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_detail=ds_detail"
        , ""
        , "fn_afterQuery"
        , "" //true
        , ""
        , true);


}
