/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	//해당데이타를 저장하시겠습니까?
	chk = 'X';
    ds_cond.SetColumn(0, "REP", "NH");
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;
	if(gfn_isNull(cb_frame,  true, Static18.value)) return;

	// fit 항목 그리드로 생성
	//ufn_makeListDs();
	// 필드 유효성 체크
	if(!ufn_verifyField()) return;
	// 데이터 셋 각 컬럼 셋팅
	if(!ufn_setSaveDs()) return;
    // 필수값 체크 	
	if(!ufn_chkDs()) return;

	//==================해당호기 존재여부체크==================
	chk = '';
	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps11:PS1107001A_S4");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_error=ds_error"
		, ""
		, "" //"fn_afterSave"
		, ""
		, ""
		, true);	

	if(ds_error.GetRowCount() > 0)
	{
		gfn_showAlert("CW00040",ds_error.GetColumn(0,"HNO") + "호기");	
		//chk = 'X';
		chk = 'Y';  //저장후 전송을 눌러도 전송은 가능하게 하기위함
		return false;
	}
	//=========================================================		

   //주행500MM 차이 종속석 추가 -- 이동욱 SW 
   if( parseInt(ds_detail.GetColumn(0, "B_2_3"))  - parseInt(ds_detail.GetColumn(0, "B_1_3"))  <= 500 )
   {  if( difchk == 'Y'){
      ds_master1.SetColumn(0,"FRAME_CK" , 'I'); 
      ds_master.SetColumn(0,"FRAME_CK" , 'I'); }
   }

   if( length(ds_master.GetColumn(0,"REQ")) > 0 || length(ds_detail.GetColumn(0,"B_4")) > 0 )
   {
    if( difchk == 'Y'){
      ds_master1.SetColumn(0,"FRAME_CK" , 'G');
      ds_master.SetColumn(0,"FRAME_CK" , 'G');}
   }


  tit_clearActionInfo();		
   if(Radio1.Index == 0 or Radio2.Index == 0)
	{

		tit_addMultiActionInfo("ps11:PS1107001A_I1");
   	    tit_addMultiActionInfo("ps11:PS1107001A_I7");
		tit_addMultiActionInfo("ps11:PS1107001A_I2");
		tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", "");

		//tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", ""); 원래주석

		tit_callService(
			""
			, ""
			, "ds_master1=ds_master1 ds_detail1=ds_detail1 ds_cond=ds_cond" 
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true);



	}

	else
	{
		tit_addMultiActionInfo("ps11:PS1107001A_I1");  
		tit_addMultiActionInfo("ps11:PS1107001A_I7");
		//tit_addMultiActionInfo("ps11:PS1107001A_I2"); 원래주석


		//tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", "");

		tit_callService(
			""
			, ""
			, "ds_master1=ds_master1 ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true);	
	}

}	



