function fn_cancel() {

	ds_master.Reset();
	ds_master1.DeleteAll();	

	ds_detail.Reset();
	ds_detail1.DeleteAll();
	// gfn_initDs(ds_cond, true);

 	// init data set
	ds_cond.SetColumn(0, "REP", "NH");
	ds_master.SetColumn(0, "BJD", gfn_getCurrDate());
	ds_master.SetColumn(0, "HDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "SNM", G_USER_NAME);	

	gfn_initDs(ds_detail, true);

	Radio1.Index = -1;
	Radio2.Index = -1;

}
function fn_clear() {
	// dataset initailize
	ds_master.DeleteAll();
	ds_master1.DeleteAll();

	ds_detail.DeleteAll();
	ds_detail1.DeleteAll();


	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_detail, true);


	// field initailize
	ed_pjt.Text = "";
	ed_hno.Text = "";
 	ed_hnos.Text = "";

 	// init data set   
	ds_master.SetColumn(0, "BJD", gfn_getCurrDate());
	ds_master.SetColumn(0, "HDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "SNM", G_USER_NAME);	

	ds_cond.SetColumn(0, "REP", "NH");
}
