function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(Edit3);

	ds_master.SetColumn(0, "HDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "SNM", G_USER_NAME);	

	if( ds_detail.rowcount <= 0 )
	{
		gfn_initDs(ds_detail, true);
	}

	fn_afterFileAdd();

//	alert(ds_master.GetColumn(0, "VALUE"));

}
