function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var lv_sum = 0;

	ds_detail.fireevent = false;
	if(strColumnID=="A_1_1" or strColumnID=="A_1_2" or strColumnID=="A_1_3" or strColumnID=="A_1_4" or
	   strColumnID=="A_2_1" or strColumnID=="A_2_2" or strColumnID=="A_2_3" or strColumnID=="A_2_4" or
	   //strColumnID=="A_3_1" or strColumnID=="A_3_2" or strColumnID=="A_3_3" or strColumnID=="A_3_4" or
	   strColumnID=="A_5_1" or strColumnID=="A_5_2" or strColumnID=="A_5_3" or strColumnID=="A_5_4" or
	   strColumnID=="A1"    or strColumnID=="A2"    or strColumnID=="A3"    or strColumnID=="A4" or
	   strColumnID=="A5"    or strColumnID=="A6"    or

	   strColumnID=="A_4_1" or strColumnID=="A_4_2" or strColumnID=="A_4_3" or strColumnID=="A_4_4"  )
	{
		if( length(ds_detail.GetColumn(nRow,"A_1_1")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A_1_2")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_1_3")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_1_4")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_1")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A_2_2")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_3")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_4")) == 0 and
		    //length(ds_detail.GetColumn(nRow,"A_3_1")) == 0 and 
		    //length(ds_detail.GetColumn(nRow,"A_3_2")) == 0 and		    
		    //length(ds_detail.GetColumn(nRow,"A_3_3")) == 0 and
		    //length(ds_detail.GetColumn(nRow,"A_3_4")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_5_1")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A_5_2")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_5_3")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_5_4")) == 0 and

		    length(ds_detail.GetColumn(nRow,"A1")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A2")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A3")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A4")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A5")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A6")) == 0 and

		    length(ds_detail.GetColumn(nRow,"A_4_1")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A_4_2")) == 0 and		    
		    length(ds_detail.GetColumn(nRow,"A_4_3")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_4_4")) == 0 		    )
		{

			Radio1.Index = 1;
		}
		else
		{			
			Radio1.Index = 0;
		}
	}
	//============================================================================
	if(//strColumnID=="B_1_1" or strColumnID=="B_1_2" or strColumnID=="B_1_3" or strColumnID=="B_1_4" or
	   strColumnID=="B_2_1" or strColumnID=="B_2_2" or strColumnID=="B_2_3" or strColumnID=="B_2_4" or 
	   strColumnID=="B_3"   or strColumnID=="B_4"
	   ) 
	{
		if( //length(ds_detail.GetColumn(nRow,"B_1_1")) == 0 and 
		    //length(ds_detail.GetColumn(nRow,"B_1_2")) == 0 and
		    //length(ds_detail.GetColumn(nRow,"B_1_3")) == 0 and
		    //ength(ds_detail.GetColumn(nRow,"B_1_4")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"B_2_1")) == 0 and
		    length(ds_detail.GetColumn(nRow,"B_2_2")) == 0 and
		    length(ds_detail.GetColumn(nRow,"B_2_3")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"B_2_4")) == 0 and		    
		    length(ds_detail.GetColumn(nRow,"B_3")) == 0   and
		    length(ds_detail.GetColumn(nRow,"B_4")) == 0
		   )
		{			
			Radio2.Index = 1;
		}
		else
		{			
			Radio2.Index = 0;
		}
	}


	//====================================승강로 전장 합계 구하기================================
	if(strColumnID=="B_2_1" or strColumnID=="B_2_2" or strColumnID=="B_2_3") // or strColumnID=="B_2_4" )
	{
		lv_sum = parseint(ds_detail.GetColumn(nRow,"B_2_1")) + 
		         parseint(ds_detail.GetColumn(nRow,"B_2_2")) + 
		         parseint(ds_detail.GetColumn(nRow,"B_2_3"));
		ds_detail.SetColumn(nRow, "B_2_4", lv_sum);
	}

	ds_detail.fireevent = true;
}


