	tit_callService(
	 ""
	, ""
	, "ds_cond=ds_cond"
	, "ds_file=ds_file"
	, ""
	, "fn_afterFileSelect"
	, ""
	, ""
	, true);
}

function fn_afterFileSelect()
{
	Trace(ds_file.saveXML());
	//필터 1 - 오버헤드
	ds_file.Filter("GBN == 'O'");
	ds_file_o.copyF(ds_file);
	ds_file.UnFilter();

	//필터 2 - 피트
	ds_file.Filter("GBN == 'P'");
	ds_file_p.copyF(ds_file);
	ds_file.UnFilter();

	//필터 3 - 기타
	ds_file.Filter("GBN == 'E'");
	ds_file_e.copyF(ds_file);
	ds_file.UnFilter();
}

