/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() 
{

	if(!ufn_verifyField()) return;
	if(!ufn_setSaveDs()) return;
    // 필수값 체크 	
	if(!ufn_chkDs()) return;
	//해당데이타를 저장하시겠습니까?
	chk = 'X';
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;
	if(gfn_isNull(cb_frame,  true, Static18.value)) return;

	chk = '';
    //주행500MM 차이 종속석 추가 -- 이동욱 SW 
   if( parseInt(ds_detail.GetColumn(0, "B_2_3"))  - parseInt(ds_detail.GetColumn(0, "B_1_3"))  <= 500 )
   {  if( difchk == 'Y'){
      ds_master1.SetColumn(0,"FRAME_CK" , 'I'); 
      ds_master.SetColumn(0,"FRAME_CK" , 'I');}
   }

   if(difchk == 'Y' && ( length(ds_master.GetColumn(0,"REQ")) > 0 ||  length(ds_detail.GetColumn(0,"B_4")) > 0 ) )
   {
      ds_master1.SetColumn(0,"FRAME_CK" , 'G');
      ds_master.SetColumn(0,"FRAME_CK" , 'G');
   }

	tit_clearActionInfo();		
	//tit_addMultiActionInfo("ps11:PS1101003B_U1");		
	//tit_addMultiActionInfo("ps11:PS1101003B_D1");
	//tit_addMultiActionInfo("ps11:PS1101003A_I2");


	if(div_pop.Radio1.Index == 0 or div_pop.Radio2.Index == 0)
	{	
		tit_addMultiActionInfo("ps11:PS1107001B_U1");		
		tit_addMultiActionInfo("ps11:PS1107001B_D1");
		tit_addMultiActionInfo("ps11:PS1107001A_I2");


		tit_callService(
			""
			, ""
			, "ds_master1=ds_master1 ds_cond=ds_cond ds_detail1=ds_detail1"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true);	
	}
	else
	{
		tit_addMultiActionInfo("ps11:PS1107001B_U1");		
		tit_addMultiActionInfo("ps11:PS1107001B_D1");
		//tit_addMultiActionInfo("ps11:PS1107001A_I2");

		tit_callService(
			""
			, ""
			, "ds_master1=ds_master1 ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true);				
	}

}	
/********************************************************************************
* 기      능   : 전송 60
********************************************************************************/
function fn_trans() 
{

    if(gfn_isNull(div_pop.cb_frame,  true, div_pop.Static18.value)) return;

	fn_save();

//trace(ds_cond.SaveXML());	
//alert(chk);	
	if (chk != 'X')
	{
	    //==============이력테이블 차수관리=============
		tit_clearActionInfo();	
		tit_addSearchActionInfo("ps11:PS1107001B_I1");	
		tit_addSearchActionInfo("ps11:PS1107001B_I2");	
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond ds_cond=ds_cond"
			, ""
			, ""
			, ""  //"fn_afterQuery"
			, "" //true
			, ""
			, true);

	    //=====================쪽지, 메일====================
		tit_clearActionInfo();	
		tit_addSearchActionInfo("ps11:PS1107001A_S5");			
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_trans=ds_trans"
			, ""
			, ""  //"fn_afterQuery"
			, "" //true
			, ""
			, true);	

		var colum;
		var row;
		var mesgdesc;
		var perno;
		var chkmsg1;
		var chkmsg2;
		var chkmsg;
		colum = ds_master.GetColumn(0,"FRAME_CK");
		colum = "I";
		row   = ds_frame.SearchRow("CODE=='"+colum+"'");
		ds_trans.SetColumn(0,"FRAME_CK", ds_frame.GetColumn(row,"CODE_NAME"));

		mesgdesc = ds_trans.GetColumn(0,"POSID") + ' (' + ds_trans.GetColumn(0,"SITE_NM") + ')' + '\n';
		mesgdesc = mesgdesc + ds_trans.GetColumn(0,"FRAME_CK") + '\n';
		mesgdesc = mesgdesc + '담당PM : ' + ds_trans.GetColumn(0,"PMNM");
		ds_trans.SetColumn(0, "MESGDESC", mesgdesc);


		if( length(ds_trans.GetColumn(0,"PERNO1")) != 0 && length(ds_trans.GetColumn(0,"PER_MAIL")) != 0  )  //영업담당이 있을경우
		{			
			//ds_trans.SetColumn(0, "PERNO", SubStr(ds_trans.GetColumn(0, "PERNO1"),1,7) );
			ds_trans.SetColumn(0, "PERNO", 'kwangyong.song@hdel.co.kr');
			perno = 'kwangyong.song@hdel.co.kr'; //테스트진행
			fn_mesg();


		    perno = ds_trans.GetColumn(0, "PER_MAIL");
		    perno = 'kwangyong.song@hdel.co.kr'; //테스트진행
			ds_trans.SetColumn(0, "PERNO",  perno);
			fn_mail();
			ds_trans.SetColumn(0, "PERNO", '' );
			chkmsg1 = 'Y';
		}

		if ( chkmsg1 == 'Y' )	{ chkmsg = '(영업담당 통보 /';}
		else {	chkmsg = '(영업담당 미통보 /';	}

		if( length(ds_trans.GetColumn(0,"PERNO2")) != 0 )  //기술영업담당이 있을경우
		{
			//ds_trans.SetColumn(0, "PERNO", SubStr(ds_trans.GetColumn(0, "PERNO2"),1,7) );
			ds_trans.SetColumn(0, "PERNO", 'kwangyong.song@hdel.co.kr' );
			perno = 'kwangyong.song@hdel.co.kr'; //테스트진행
			fn_mesg();
			perno = 'helco' + SubStr(ds_trans.GetColumn(0, "PERNO2"),1,7) + '@hdel.co.kr';

			perno = 'kwangyong.song@hdel.co.kr'; //테스트진행
			ds_trans.SetColumn(0, "PERNO",  perno);
			fn_mail();
			ds_trans.SetColumn(0, "PERNO", '' );
			chkmsg2 = 'Y';
		}

		if ( chkmsg2 == 'Y' )	{	chkmsg = chkmsg + ' 기술영업담당 통보) ';		}
		else {	chkmsg = chkmsg + ' 기술영업담당 미통보) ';		}

		gfn_showAlert("CI00006", chkmsg);	
    }

}

