function fn_mesg()
{

	return;
//alert("xxx");

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps11:PS1107001A_I6");			
	tit_callService(
		""
		, ""
		, "ds_trans=ds_trans"
		, ""
		, ""
		, ""  //"fn_afterQuery"
		, "" //true
		, ""
		, true);

}




/********************************************************************************
* 기      능   : mail
********************************************************************************/
function fn_mail()
{
	trace(ds_trans.GetColumn(0,"USER"));
	trace(ds_trans.GetColumn(0,"PERNO"));
	trace(ds_trans.GetColumn(0,"MESGTITL"));
	trace(ds_trans.GetColumn(0,"MESGDESC"));


	tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=" + quote( ds_trans.GetColumn(0,"USER") )
				+ " RECE_USER=" + quote( ds_trans.GetColumn(0,"PERNO") )
				+ " MESGTITL=" + quote( ds_trans.GetColumn(0,"MESGTITL") )
				+ " MESGBODY=" + quote( ds_trans.GetColumn(0,"MESGDESC") )
			, ""  //"fn_afterQuery"
			, false
			, true);	



}



