function ufn_verifyField(gbn){
	// 해더 부분
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static0.Text)) return false; // 프로젝트번호
	if(gfn_isNull(div_pop.Edit9, true, div_pop.Static5.Text)) return false;	// 실측자
	if(gfn_isNull(div_pop.Calendar2, true, div_pop.Static3.Text)) return false; // 현장요구일자


	if(div_pop.Radio1.Index==-1) {
		//gfn_showMsg("CW00001", Static12.Text);
		gfn_showAlert("CW00001", Static12.Text);
		return false;
	}

	if(div_pop.Radio2.Index==-1) {
		//gfn_showMsg("CW00001", Static13.Text);
		gfn_showAlert("CW00001", Static13.Text);
		return false;
	}



	return true;	
}


function ufn_setSaveDs()
{
	var mrow = 0;


	ds_master1.DeleteAll();
	ds_detail1.DeleteAll();

	if(length(div_pop.ed_hnos.Text)>0 && indexOf(div_pop.ed_hnos.text, ",")!=-1){
		var hnos = Split(div_pop.ed_hnos.text, ",");

		for(var i=0; i<length(hnos); i++){
			if(length(hnos[i])==3){
				mrow = ds_master1.AddRow();
				ds_master1.CopyRow(mrow, ds_master, 0);			
				ds_master1.SetColumn(mrow, "HNO", hnos[i]);
				ds_master1.SetColumn(mrow, "PJT", div_pop.ed_pjt.Text);

				ufn_setListDs(div_pop.ed_pjt.Text, hnos[i]);
			}					
		}		

	} 
	else 
	{ 
		if(gfn_isNull(div_pop.ed_hno, true, div_pop.Static2.Text)) return false;

		mrow = ds_master1.AddRow();
		ds_master1.CopyRow(mrow, ds_master, 0);	
		ds_master1.SetColumn(mrow, "PJT", div_pop.ed_pjt.Text);
		ds_master1.SetColumn(mrow, "HNO", div_pop.ed_hno.Text);

		ufn_setListDs(div_pop.ed_pjt.Text, div_pop.ed_hno.Text);
	}

	return true;
}



function ufn_setListDs(pjt, hno)
{

	var crow=0;
	var cseq;

	//for(var j=0; j<ds_list1.GetRowCount(); j++){
	//	crow = ds_list2.AddRow();
	//	ds_list2.CopyRow(crow, ds_list1, j);
	//	ds_list2.SetColumn(crow, "CESDCPJT", pjt);
	//	ds_list2.SetColumn(crow, "CESDCHNO", hno);
	//	cseq = Lpad(j+1, "0", 3);
	//	ds_list2.SetColumn(crow, "CESDCSEQ", cseq);		
	//}



	// 디테일 데이터셋 셋팅
	if(div_pop.Radio1.Index==0 or div_pop.Radio2.Index==0)
	{

		crow = ds_detail1.AddRow();		


		ds_detail1.CopyRow(crow, ds_detail, 0);			

		ds_detail1.SetColumn(crow, "PJT", pjt);
		ds_detail1.SetColumn(crow, "HNO", hno);

		if(div_pop.Radio1.Index==1)
		{
			ds_detail1.SetColumn(crow, "A_1_1", "");
			ds_detail1.SetColumn(crow, "A_1_2", "");
