function fn_print() 
{
	tit_clearActionInfo();		
	//tit_addMultiActionInfo("ps11:PS1101003B_U2");		
	//tit_addMultiActionInfo("ps11:PS1107001B_U2");		
	tit_addSelectExActionInfo("ps11:PS1107001B_S4","ps11:PS1107001B_U2", "ps11:PS1107001B_U3");
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterPrint"
		, ""
		, ""
		, true);	
}

function fn_print2() 
{
	ds_master_p.DeleteAll();
	ds_detail_p.DeleteAll();
	ds_master_p.AddRow();
	ds_detail_p.AddRow();

	// 저장할 데이터셋을 복사
	ds_master_p.CopyRow(0,"ds_master",0);
	ds_master_p.SetColumn(0,"HDM",gfn_formatDate(ds_master_p.GetColumn(0,"HDM"),'.'));
	ds_master_p.SetColumn(0,"BJD",gfn_formatDate(ds_master_p.GetColumn(0,"BJD"),'.'));
	ds_master_p.SetColumn(0,"NAP",gfn_formatDate(ds_master_p.GetColumn(0,"NAP"),'.'));

	var colum;
	var row;
	colum = ds_master.GetColumn(0,"FRAME_CK");
	row   = ds_frame.SearchRow("CODE=='"+colum+"'");
	ds_master_p.SetColumn(0,"FRAME_CK", ds_frame.GetColumn(row,"CODE_NAME"));
//alert(	ds_frame.GetColumn(row,"CODE_NAME")  );

	if (ds_master_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	ds_detail_p.CopyRow(0,"ds_detail",0);

    /*
	ds_detail_p.SetColumn(0,"PIT",object("div_pop.Edit1").text);
	ds_detail_p.SetColumn(0,"CESDBBAD",ds_master_p.GetColumn(0,"CESDBBAD"));
	ds_detail_p.SetColumn(0,"CESDBBBD",ds_master_p.GetColumn(0,"CESDBBBD"));
	ds_detail_p.SetColumn(0,"CESDBBCD",ds_master_p.GetColumn(0,"CESDBBCD"));
	ds_detail_p.SetColumn(0,"CESDBBAS",ds_master_p.GetColumn(0,"CESDBBAS"));
	ds_detail_p.SetColumn(0,"CESDBBBS",ds_master_p.GetColumn(0,"CESDBBBS"));
	ds_detail_p.SetColumn(0,"CESDBBCS",ds_master_p.GetColumn(0,"CESDBBCS"));
	*/

	// UbiReport Call
	Dialog("PS11::PS1107001P.xml", "");
}

