/********************************************************************************
* 기      능   : 저장 전 필수값 체크
********************************************************************************/
function ufn_chkDs()
{
   difchk = 'Y'; // 승강로 X,Y치수가 상이하거나, 피트 및 정상부가 상이할 경우 체크 

//승강로 x / y(재질별)

 if(ds_master.GetColumn(0, "VALUE") == "CEMEN") 
 {
   if(length(trim(ds_detail.GetColumn(0, "A_5_1")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_6_1")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_2_1")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_3_1")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_5_2"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_2"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_3"))) > 0
   || length(trim(ds_detail.GetColumn(0, "A_2_3"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_4"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_4"))) > 0 )
   {
      gfn_showMsg("CW10025","해당 현장의 승강로 재질은 CEMEN 입니다. 해당 되는 재질의 정보를 입력해주십시요.");
	  return;
   }
   //x.y치수 체크 
   if((trim(ds_detail.GetColumn(0, "A_5_1")) <> trim(ds_detail.GetColumn(0, "A_6_1"))) || (trim(ds_detail.GetColumn(0, "A_2_1")) <> trim(ds_detail.GetColumn(0, "A_3_1"))) )
   {
     difchk = 'X';
   }


 }
 else if(ds_master.GetColumn(0, "VALUE") == "STWL") 
 {
   if(length(trim(ds_detail.GetColumn(0, "A_5_2")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_6_2")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_2_2")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_3_2")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_5_1"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_1"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_3"))) > 0
   || length(trim(ds_detail.GetColumn(0, "A_2_3"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_4"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_4"))) > 0 )
   {
      gfn_showMsg("CW10025","해당 현장의 승강로 재질은 STWL 입니다. 해당 되는 재질의 정보를 입력해주십시요.");
	  return;
   }
      //x.y치수 체크 
   if((trim(ds_detail.GetColumn(0, "A_5_2")) <> trim(ds_detail.GetColumn(0, "A_6_2"))) || (trim(ds_detail.GetColumn(0, "A_2_2")) <> trim(ds_detail.GetColumn(0, "A_3_2"))) )
   {
     difchk = 'X';
   }
 }
 else if(ds_master.GetColumn(0, "VALUE") == "STGL") 
 {
   if(length(trim(ds_detail.GetColumn(0, "A_5_3")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_6_3")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_2_3")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_3_3")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_5_2"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_2"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_1"))) > 0
   || length(trim(ds_detail.GetColumn(0, "A_2_1"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_4"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_4"))) > 0 )
   {
      gfn_showMsg("CW10025","해당 현장의 승강로 재질은 STGL 입니다. 해당 되는 재질의 정보를 입력해주십시요.");
	  return;
   }
   //x.y치수 체크 
   if((trim(ds_detail.GetColumn(0, "A_5_3")) <> trim(ds_detail.GetColumn(0, "A_6_3"))) || (trim(ds_detail.GetColumn(0, "A_2_3")) <> trim(ds_detail.GetColumn(0, "A_3_3"))) )
   {
     difchk = 'X';
   }

 }
 else if(ds_master.GetColumn(0, "VALUE") == "STSP") 
 {
   if(length(trim(ds_detail.GetColumn(0, "A_5_4")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_6_4")))<= 0) {
     gfn_showMsg("CW00001","승강로 X(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_2_4")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(실측치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_3_4")))<= 0) {
     gfn_showMsg("CW00001","승강로 Y(도면치수)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "A_5_2"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_2"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_3"))) > 0
   || length(trim(ds_detail.GetColumn(0, "A_2_3"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_5_1"))) > 0 || length(trim(ds_detail.GetColumn(0, "A_2_1"))) > 0 )
   {
      gfn_showMsg("CW10025","해당 현장의 승강로 재질은 STSP 입니다. 해당 되는 재질의 정보를 입력해주십시요.");
	  return;
   }
    //x.y치수 체크 
   if((trim(ds_detail.GetColumn(0, "A_5_4")) <> trim(ds_detail.GetColumn(0, "A_6_4"))) || (trim(ds_detail.GetColumn(0, "A_2_4")) <> trim(ds_detail.GetColumn(0, "A_3_4"))) )
   {
     difchk = 'X';
   }
 }

   //승강로 전장
    if(length(trim(ds_detail.GetColumn(0, "B_2_1")))<= 0) {
     gfn_showMsg("CW00001","피트");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "B_2_2")))<= 0) {
     gfn_showMsg("CW00001","정상부(오버헤드)");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "B_2_3")))<= 0) {
     gfn_showMsg("CW00001","주행거리");
	 return;
   }
   if(length(trim(ds_detail.GetColumn(0, "B_2_4")))<= 0) {
     gfn_showMsg("CW00001","승강로 전장");
	return;
   }
    //피트 정상부 체크 
    if((trim(ds_detail.GetColumn(0, "B_1_1")) <> trim(ds_detail.GetColumn(0, "B_2_1"))) || (trim(ds_detail.GetColumn(0, "B_1_2")) <> trim(ds_detail.GetColumn(0, "B_2_2"))) )
   {
     difchk = 'X';
   }

  // 오버헤드, 피트 및 승강로 X,Y 등 다른 치수에서 하나라도 상이한 부분이 있을 경우는 
   if( difchk == 'X' && ds_master1.GetColumn(0,"FRAME_CK" ) == 'I')
   {  
      gfn_showMsg("CW10025","오버헤드, 피트 및 승강로 X,Y 등 다른 치수에서 하나라도 상이한 부분이 있을 경우는 골조문제점 이상없음이 불가합니다.");
      return ;
   }

  return true;
}
