function fn_OnLoadCompleted(obj) {
	//gfn_initForm(true);	
	gfn_initForm();	
	fn_clear();


	for ( var b = 0; b < Length(this.Components); b++ ) 
	{
		comp = this.components(b);
		comp.id.enable = false;
	}



	// 조회 화면
	//ed_pjt.Text = PRE_PJT;
	//ed_hno.Text = PRE_HNO;

	// grid 1, 2 setting
	ufn_setGrid();




}

