	ds_list2.DeleteAll();

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps11:PS1107002A_S1");
	tit_addSearchActionInfo("ps11:PS1107004A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master"  // ds_list1=ds_list1"
        , ""
        , "fn_afterQuery"
        , ""  //true
        , ""
        , true);
}
