/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(!ufn_verifyField()) return;
	if(!ufn_setSaveDs()) return;

	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	//==================해당호기 존재여부체크==================
	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps11:PS1107004A_S2");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_error=ds_error"
		, ""
		, "" //"fn_afterSave"
		, ""
		, ""
		, true);	

	if(ds_error.GetRowCount() > 0)
	{
		gfn_showAlert("CW00040",ds_error.GetColumn(0,"HNO") + "호기");	
		return false;
	}
	//=========================================================		



	tit_clearActionInfo();		

	//tit_addMultiActionInfo("ps11:PS1107002A_I1");		
	tit_addMultiActionInfo("ps11:PS1107004A_I1");		

	if(Radio1.Index == 0 and Radio2.Index == 0)
	{
		//tit_addMultiActionInfo("ps11:PS1107002A_I2");
		//tit_addMultiActionInfo("ps11:PS1107002A_I3");
		//tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", "");
		tit_addMultiActionInfo("ps11:PS1107004A_I2");
		tit_addMultiActionInfo("ps11:PS1107004A_I3");
		tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", "");
	}

	if(Radio1.Index == 0 and Radio2.Index == 1)
	{
		//tit_addMultiActionInfo("ps11:PS1107002A_I2");
		tit_addMultiActionInfo("ps11:PS1107004A_I2");
		tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", "");
	}

	if(Radio1.Index == 1 and Radio2.Index == 0)
	{
		//tit_addMultiActionInfo("ps11:PS1107002A_I3");
		tit_addMultiActionInfo("ps11:PS1107004A_I3");
		tit_addSelectExActionInfo("ps11:PS1107001A_S3", "ps11:PS1107001A_I3", "");
	}

	tit_callService(
		""
		, ""
		, "ds_master_t=ds_master_t ds_list1_t2=ds_list1_t2 ds_list2_t2=ds_list2_t2 ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true);	
}	
