function fn_cancel() {

	ds_master.reset();
	ds_master_t.DeleteAll();	

	ds_list1.reset();
	ds_list2.reset();

	ds_list1_t.DeleteAll();
	ds_list2_t.DeleteAll();

	ds_list1_t2.DeleteAll();
	ds_list2_t2.DeleteAll();

	// gfn_initDs(ds_cond, true);

 	// init data set
	ds_cond.SetColumn(0, "REP", "NR");
	ds_master.SetColumn(0, "BJD", gfn_getCurrDate());
	ds_master.SetColumn(0, "HDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "SNM", G_USER_NAME);
	ufn_setGrid();

	//Radio1.Index = -1;
	//Radio2.Index = -1;
	var lv_chk = '';	
	for(var j=0; j<ds_list1.GetRowCount(); j++)
	{
		if( length(ds_list1.GetColumn(j,"BMLONG")) == 0 and 
		    length(ds_list1.GetColumn(j,"BMCNT")) == 0 )
		{

		}
		else
		{
			lv_chk = 'X';
		}		
	}
	if (lv_chk == 'X')
	{
		Radio1.Index = 0;
	}
	else
	{
		Radio1.Index = 1;
	}
	//=======================================================================
	lv_chk = '';
	for(var j=0; j<ds_list2.GetRowCount(); j++)
	{
		if( length(ds_list2.GetColumn(j,"CAR_L")) == 0 and 
		    length(ds_list2.GetColumn(j,"CAR_R")) == 0 and
		    length(ds_list2.GetColumn(j,"CAR_AMT")) == 0 and
		    length(ds_list2.GetColumn(j,"CWT_L")) == 0 and
		    length(ds_list2.GetColumn(j,"CWT_R")) == 0 and
		    length(ds_list2.GetColumn(j,"CWT_AMT")) == 0 )
		{

		}
		else
		{
			lv_chk = 'X';
		}		
	}
	if (lv_chk == 'X')
	{
		Radio2.Index = 0;
	}
	else
	{
		Radio2.Index = 1;
	}


}

function fn_clear() {

	ds_master.DeleteAll();
	ds_master_t.DeleteAll();	

	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	ds_list1_t.DeleteAll();
	ds_list2_t.DeleteAll();

	ds_list1_t2.DeleteAll();
	ds_list2_t2.DeleteAll();

	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_list1, true);
	gfn_initDs(ds_list2, true);

	// field initailize
	ed_pjt.Text = "";
	ed_hno.Text = "";
 	ed_hnos.Text = "";

 	// init data set  G_USER_NAME
	ds_master.SetColumn(0, "BJD", gfn_getCurrDate());
	ds_master.SetColumn(0, "HDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "SNM", G_USER_NAME);

	ds_cond.SetColumn(0, "REP", "NR");
}
