/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅 
********************************************************************************/
function ufn_setGrid()
{
	grd_list1.Redraw = false;
	ds_list1.fireEvent = false;
	var rows;
	var cBKN ;
	while(21 > ds_list1.GetRowCount()-1){
		rows = ds_list1.InsertRow( ds_list1.GetRowCount() -1 );
		cBKN = lpad(rows+1,"0",3);	//rows+1;
		ds_list1.SetColumn(rows, "LNO", cBKN);	
	}
	ds_list1.DeleteRow(21);
	ds_list1.row = 0;
	ds_list1.fireEvent = true;	
	grd_list1.Redraw = true;

	//========================================================================

	grd_list2.Redraw = false;
	ds_list2.fireEvent = false;
	rows = 0;
	cBKN = 0;
	while(20 > ds_list2.GetRowCount()-1){
		rows = ds_list2.InsertRow( ds_list2.GetRowCount() -1 );
		cBKN = lpad(rows+1,"0",3);	//rows+1;
		ds_list2.SetColumn(rows, "LNO", cBKN);	
	}
	ds_list2.DeleteRow(20);
	ds_list2.row = 0;
	ds_list2.fireEvent = true;	
	grd_list2.Redraw = true;	


}



function ufn_verifyField(gbn)
{
	// 해더 부분
	if(gfn_isNull(ed_pjt, true, Static0.Text)) return false; // 프로젝트번호
	if(gfn_isNull(Edit9, true, Static5.Text)) return false;	// 실측자
	if(gfn_isNull(Calendar2, true, Static3.Text)) return false; // 현장요구일자

	if(Radio1.Index==-1) {
		//gfn_showMsg("CW00001", Static12.Text);
		gfn_showAlert("CW00001", Static12.Text);
		return false;
	}

	if(Radio2.Index==-1) {
		//gfn_showMsg("CW00001", Static13.Text);
		gfn_showAlert("CW00001", Static13.Text);
		return false;
	}




	// 리스트 부분
	var curFlr;
	var seaRow;

	ds_list1_t.DeleteAll();

	ds_list1.Filter("CHECK=='1'");
	ds_list1_t.copyf(ds_list1);
	ds_list1.UnFilter();

	if(ds_list1_t.GetRowCount()==0 and Radio1.Index==0)
	{
		gfn_showMsg("CW00001","SEPERARING BEAM(중간빔)값을 하나이상 입력해 주십시오.");	
		return false;
	}

	//===============================================================
	ds_list2_t.DeleteAll();

	ds_list2.Filter("CHECK=='1'");
	ds_list2_t.copyf(ds_list2);
	ds_list2.UnFilter();

	if(ds_list2_t.GetRowCount()==0 and Radio2.Index==0)
	{
		gfn_showMsg("CW00001","2ND RAIL BRKT값을 하나이상 입력해 주십시오.");	
		return false;
	}




	return true;	
}


function ufn_setSaveDs(){
	var mrow = 0;

	ds_master_t.DeleteAll();
	ds_list1_t2.DeleteAll();
	ds_list2_t2.DeleteAll();

	if(length(ed_hnos.Text)>0 && indexOf(ed_hnos.text, ",")!=-1){
		var hnos = Split(ed_hnos.text, ",");

		for(var i=0; i<length(hnos); i++){
			if(length(hnos[i])==3){
				mrow = ds_master_t.AddRow();
				ds_master_t.CopyRow(mrow, ds_master, 0);			
				ds_master_t.SetColumn(mrow, "HNO", hnos[i]);
				ds_master_t.SetColumn(mrow, "PJT", ed_pjt.Text);

				ufn_setListDs(ed_pjt.Text, hnos[i]);
			}					
		}		

	} else { 
		if(gfn_isNull(ed_hno, true, Static2.Text)) return false;

		mrow = ds_master_t.AddRow();
		ds_master_t.CopyRow(mrow, ds_master, 0);	
		ds_master_t.SetColumn(mrow, "PJT", ed_pjt.Text);
		ds_master_t.SetColumn(mrow, "HNO", ed_hno.Text);

		ufn_setListDs(ed_pjt.Text, ed_hno.Text);
	}

	return true;
}


function ufn_setListDs(pjt, hno)
{
	var crow=0;
	var cseq;
	/*
	for(var j=0; j<ds_list1.GetRowCount(); j++){
		crow = ds_list2.AddRow();
		ds_list2.CopyRow(crow, ds_list1, j);
		ds_list2.SetColumn(crow, "CESDUPJT", pjt);
		ds_list2.SetColumn(crow, "CESDUHNO", hno);

		//cseq = j+1; 
		cseq = Lpad(j+1, "0", 3);	
		ds_list2.SetColumn(crow, "CESDUBKN", cseq);		
	}
	*/

	// 디테일 데이터셋 셋팅
	if(Radio1.Index==0)
	{

		for(var j=0; j<ds_list1_t.GetRowCount(); j++)
		{
			crow = ds_list1_t2.AddRow();
			ds_list1_t2.CopyRow(crow, ds_list1_t, j);
			ds_list1_t2.SetColumn(crow, "PJT", pjt);
			ds_list1_t2.SetColumn(crow, "HNO", hno);

			//cseq = j+1; 
			cseq = Lpad(j+1, "0", 3);	
			ds_list1_t2.SetColumn(crow, "LNO", cseq);	
		}

	}

	if(Radio2.Index==0)
	{

		for(var j=0; j<ds_list2_t.GetRowCount(); j++)
		{
			crow = ds_list2_t2.AddRow();
			ds_list2_t2.CopyRow(crow, ds_list2_t, j);
			ds_list2_t2.SetColumn(crow, "PJT", pjt);
			ds_list2_t2.SetColumn(crow, "HNO", hno);

			//cseq = j+1; 
			cseq = Lpad(j+1, "0", 3);	
			ds_list2_t2.SetColumn(crow, "LNO", cseq);	
		}		

	}




}



