/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	ds_list1.fireevent = false;
	if(strColumnID=="BMLONG" or strColumnID=="BMCNT")
	{
		if( length(ds_list1.GetColumn(nRow,"BMLONG")) == 0 and 		    
		    length(ds_list1.GetColumn(nRow,"BMCNT")) == 0 )
		{
			ds_list1.SetColumn(nRow, "CHECK", 0);
			//Radio1.Index = 1;
		}
		else
		{
			ds_list1.SetColumn(nRow, "CHECK", 1);
			//Radio1.Index = 0;
		}
	}
	ds_list1.fireevent = true;

	//=====================================================
	var lv_chk = '';
	for(var j=0; j<ds_list1.GetRowCount(); j++)
	{
		if( length(ds_list1.GetColumn(j,"BMLONG")) == 0 and 
		    length(ds_list1.GetColumn(j,"BMCNT")) == 0 )
		{

		}
		else
		{
			lv_chk = 'X';
		}		
	}
	if (lv_chk == 'X')
	{
		Radio1.Index = 0;
	}
	else
	{
		Radio1.Index = 1;
	}
	//=====================================================

}


function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	ds_list2.fireevent = false;
	if(strColumnID=="CAR_L" or strColumnID=="CAR_R" or strColumnID=="CAR_AMT" or 
	   strColumnID=="CWT_L" or strColumnID=="CWT_R" or strColumnID=="CWT_AMT")
	{
		if( length(ds_list2.GetColumn(nRow,"CAR_L")) == 0 and 
		    length(ds_list2.GetColumn(nRow,"CAR_R")) == 0 and
		    length(ds_list2.GetColumn(nRow,"CAR_AMT")) == 0 and
		    length(ds_list2.GetColumn(nRow,"CWT_L")) == 0 and
		    length(ds_list2.GetColumn(nRow,"CWT_R")) == 0 and
		    length(ds_list2.GetColumn(nRow,"CWT_AMT")) == 0 )
		{
			ds_list2.SetColumn(nRow, "CHECK", 0);
			//Radio2.Index = 1;
		}
		else
		{
			ds_list2.SetColumn(nRow, "CHECK", 1);
			//Radio2.Index = 0;
		}
	}
	ds_list2.fireevent = true;	

	//=====================================================
	var lv_chk = '';
	for(var j=0; j<ds_list2.GetRowCount(); j++)
	{
		if( length(ds_list2.GetColumn(j,"CAR_L")) == 0 and 
		    length(ds_list2.GetColumn(j,"CAR_R")) == 0 and
		    length(ds_list2.GetColumn(j,"CAR_AMT")) == 0 and
		    length(ds_list2.GetColumn(j,"CWT_L")) == 0 and
		    length(ds_list2.GetColumn(j,"CWT_R")) == 0 and
		    length(ds_list2.GetColumn(j,"CWT_AMT")) == 0 )
		{

		}
		else
		{
			lv_chk = 'X';
		}		
	}
	if (lv_chk == 'X')
	{
		Radio2.Index = 0;
	}
	else
	{
		Radio2.Index = 1;
	}
	//=====================================================


}



