}



/********************************************************************************
* 기      능   : radio 버튼 이벤트
********************************************************************************/
function Radio1_OnClick(obj)
{
	if(Radio1.Index == 1)
	{
		ds_list1.fireevent = false;	

		gfn_initDs(ds_list1, true);

		var rows;
		var cBKN ;
		while(21 > ds_list1.GetRowCount()-1)
		{
			rows = ds_list1.InsertRow( ds_list1.GetRowCount() -1 );
			cBKN = lpad(rows+1,"0",3);	//rows+1;
			ds_list1.SetColumn(rows, "LNO", cBKN);	
		}
		ds_list1.DeleteRow(21);
		ds_list1.row = 0;


		ds_list1.fireevent = true;
	}		
}

function Radio2_OnClick(obj)
{
	if(Radio2.Index == 1)
	{
		ds_list2.fireevent = false;	

		gfn_initDs(ds_list2, true);

		var rows;
		var cBKN ;
		while(20 > ds_list2.GetRowCount()-1)
		{
			rows = ds_list2.InsertRow( ds_list2.GetRowCount() -1 );
			cBKN = lpad(rows+1,"0",3);	//rows+1;
			ds_list2.SetColumn(rows, "LNO", cBKN);	
		}
		ds_list2.DeleteRow(20);
		ds_list2.row = 0;


		ds_list2.fireevent = true;
	}	
}


/********************************************************************************
* 기      능   : (+)버튼 클릭시
********************************************************************************/
function btn_add_OnClick(obj)
{
	var rows;
	var cBKN ;
	grd_list2.Redraw = false;
	ds_list2.fireevent = false;
	//ds_list2.AddRow();	

	rows = ds_list2.InsertRow( ds_list2.GetRowCount()  );
//alert(	rows );	
	cBKN = lpad(rows+1,"0",3);	//rows+1;
//alert(	cBKN );		
	ds_list2.SetColumn(ds_list2.currow, "LNO", cBKN);


	ds_list2.fireevent = true;	
	grd_list2.Redraw = true;
}


/********************************************************************************
* 기      능   : (-)버튼 클릭시
********************************************************************************/


function ufn_getUrl() {
	var url = 'http://203.242.40.61:55002/MobileServer/map/index.jsp?m=';
	if (IndexOf(G_SERVER_URL, 'localhost') > 0 || IndexOf(G_SERVER_URL, '203.242.37.24') > 0) {
		url = 'http://203.242.40.209:8090/MobileServer/map/index.jsp?m=';
	}
	url += ds_master.getColumn(0, "POSID");
	return url;
}

/********************************************************************************
* 기      능   : 지도보기
********************************************************************************/
function btnGoUrl_OnClick(obj)
{
	if (Length(Trim(ds_master.getColumn(0, "POSID"))) == 0) {
		gfn_showAlert("CI30001", "프로젝트를 조회하십시오.");
		return;
	}
	if (Length(Trim(ds_master.getColumn(0, "G_ADDR"))) == 0) {
		gfn_showAlert("CI30001", "설정된 구글맵이 없습니다.");
		return;
	}

	ExecBrowser(ufn_getUrl());
}
