/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(!ufn_verifyField()) return;
	if(!ufn_setSaveDs()) return;

	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	tit_clearActionInfo();		


	//tit_addMultiActionInfo("ps11:PS1107002B_U1");	
	//tit_addMultiActionInfo("ps11:PS1107002B_D1");  //ZPST11072D	
	//tit_addMultiActionInfo("ps11:PS1107002B_D3");  //ZPST11072S	
	tit_addMultiActionInfo("ps11:PS1107004B_U1");	
	tit_addMultiActionInfo("ps11:PS1107004B_D1");  //ZPST11074D	
	tit_addMultiActionInfo("ps11:PS1107004B_D3");  //ZPST11074S	

	if(div_pop.Radio1.Index == 0 and div_pop.Radio2.Index == 0)
	{
		//tit_addMultiActionInfo("ps11:PS1107002A_I2"); //ZPST11072D	
		//tit_addMultiActionInfo("ps11:PS1107002A_I3"); //ZPST11072S	
		tit_addMultiActionInfo("ps11:PS1107004A_I2"); //ZPST110724	
		tit_addMultiActionInfo("ps11:PS1107004A_I3"); //ZPST110724	
	}

	if(div_pop.Radio1.Index == 0 and div_pop.Radio2.Index == 1)
	{
		//tit_addMultiActionInfo("ps11:PS1107002A_I2"); //ZPST11072D	
		tit_addMultiActionInfo("ps11:PS1107004A_I2"); //ZPST110724	
	}

	if(div_pop.Radio1.Index == 1 and div_pop.Radio2.Index == 0)
	{
		//tit_addMultiActionInfo("ps11:PS1107002A_I3"); //ZPST11072S	
		tit_addMultiActionInfo("ps11:PS1107004A_I3"); //ZPST110724	
	}

	tit_callService(
		""
		, ""
		, "ds_master_t=ds_master_t ds_cond=ds_cond ds_list1_t2=ds_list1_t2 ds_list2_t2=ds_list2_t2 "
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true);	
}	
