function fn_cancel() {
	// dataset initailize
	ds_master.reset();
	ds_master_t.DeleteAll();

	ds_list1.reset();
	ds_list2.reset();

	ds_list1_t.DeleteAll();
	ds_list2_t.DeleteAll();

	ds_list1_t2.DeleteAll();
	ds_list2_t2.DeleteAll();

	// gfn_initDs(ds_cond, true);

	/*
	for(var i=0; i<ds_rail.GetRowCount(); i++){
		ds_rail.SetColumn(i, "CHECK", 0);
	}
	*/ 	

 	// init data set
	ds_cond.SetColumn(0, "REP", "NR");
	ufn_setGrid();
}

function fn_clear() {
	ds_master.DeleteAll();
	ds_master_t.DeleteAll();

	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	ds_list1_t.DeleteAll();
	ds_list2_t.DeleteAll();

	ds_list1_t2.DeleteAll();
	ds_list2_t2.DeleteAll();

	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_list1, true);
	gfn_initDs(ds_list2, true);

	// field initailize
	div_pop.ed_pjt.Text = "";
	div_pop.ed_hno.Text = "";
 	div_pop.ed_hnos.Text = "";

 	// init data set
	ds_master.SetColumn(0, "BJD", gfn_getCurrDate());
	ds_cond.SetColumn(0, "REP", "NR");
}
