/********************************************************************************
* 기      능   : radio 버튼 이벤트
********************************************************************************/
function Radio1_OnClick(obj)
{


	if(div_pop.Radio1.Index == 1)
	{

		ds_list1.fireevent = false;	

		gfn_initDs(ds_list1, true);

		var rows;
		var cBKN ;
		while(21 > ds_list1.GetRowCount()-1)
		{
			rows = ds_list1.InsertRow( ds_list1.GetRowCount() -1 );
			cBKN = lpad(rows+1,"0",3);	//rows+1;
			ds_list1.SetColumn(rows, "LNO", cBKN);	
		}
		ds_list1.DeleteRow(21);
		ds_list1.row = 0;


		ds_list1.fireevent = true;
	}		
}

function Radio2_OnClick(obj)
{
	if(div_pop.Radio2.Index == 1)
	{
		ds_list2.fireevent = false;	

		gfn_initDs(ds_list2, true);

		var rows;
		var cBKN ;
		while(20 > ds_list2.GetRowCount()-1)
		{
			rows = ds_list2.InsertRow( ds_list2.GetRowCount() -1 );
			cBKN = lpad(rows+1,"0",3);	//rows+1;
			ds_list2.SetColumn(rows, "LNO", cBKN);	
		}
		ds_list2.DeleteRow(20);
		ds_list2.row = 0;


		ds_list2.fireevent = true;
	}	
}

/********************************************************************************
* 기      능   : (+)버튼 클릭시
********************************************************************************/
function btn_add_OnClick(obj)
{
	var rows;
	var cBKN ;
	div_pop.grd_list2.Redraw = false;
	ds_list2.fireevent = false;
	//ds_list2.AddRow();	

	rows = ds_list2.InsertRow( ds_list2.GetRowCount()  ) ;  //+ 1;
//alert(	rows );
	cBKN = lpad(rows+1,"0",3);	//rows+1;
//alert(	cBKN );	
	ds_list2.SetColumn(ds_list2.currow, "LNO", cBKN);


	ds_list2.fireevent = true;	
	div_pop.grd_list2.Redraw = true;
}


