function fn_print() 
{
	tit_clearActionInfo();			
	//tit_addMultiActionInfo("ps11:PS1107002B_U2");		
	//tit_addMultiActionInfo("ps11:PS1107004B_U2");		
	tit_addSelectExActionInfo("ps11:PS1107004B_S5","ps11:PS1107004B_U2", "ps11:PS1107004B_U3");
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterPrint"
		, ""
		, ""
		, true);	
}

