	ds_cond.SetColumn(0, "POSID", POSID);

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps11:PS1107001B_S1");
	//tit_addSearchActionInfo("ps11:PS1107001B_S2");
	tit_addSearchActionInfo("ps11:PS1108001B_S1");
	tit_addSearchActionInfo("ps11:PS1108001B_S2");

