        ""
        , ""
        , "ds_cond=ds_cond"
        //, "ds_master=ds_master ds_list=ds_list"
        , "ds_master=ds_master ds_detail=ds_detail"        
        , ""
        , "fn_afterQuery"
        //, true
        , ""
        , ""
        , true);
}
