function fn_clear() 
{

	// dataset initailize
	ds_master.DeleteAll();
	ds_master1.DeleteAll();	

	ds_detail.DeleteAll();
	ds_detail1.DeleteAll();


	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_detail, true);

	// field initailize
	div_pop.ed_pjt.Text = "";
	div_pop.ed_hno.Text = "";
 	//div_pop.ed_hnos.Text = "";

 	// init data set
	ds_master.SetColumn(0, "BJD", today());
	ds_cond.SetColumn(0, "REP", "NH");

}
