/********************************************************************************
* 기      능   : 파일 추가
********************************************************************************/
function btn_fileAdd_OnClick(obj)
{

	var fileName = "";
	var sPjtNo = ds_master.GetColumn(0, "PJT");
	var sHno   = ds_master.GetColumn(0, "HNO");
	var sGbn   = "";

	if(sPjtNo == '' || length(sPjtNo) == 0 || sHno == '' || length(sHno) == 0)
	{
		gfn_openMsgWin("Proj NO 및 호기가 존재하지 않습니다.");
		return;
	}

	if(obj.id == 'btn_fileAdd')	// 오버헤드
	{
		sGbn = 'O';
	}
	else if(obj.id == 'btn_fileAdd2')	// 피트
	{
		sGbn = 'P';
	}
	else // Add3 : 기타(ETC)
	{
		sGbn = 'E';
	}


	/*
	if(ds_file.rowcount >= 6)
	{
		gfn_openMsgWin("추가할 수 있는 파일 개수를 초과했습니다.\n (최대 6개 저장가능)","","A");
		return;
	}
	*/
	var sRtn = fileDialog.open();

	if(sRtn == 0) return;


	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "10 Mbytes" );	
		return;
	}


	// 파일정보
	if(length(fileDialog.FileName) > 0 )
	{
		var thumbext = fileDialog.FileName; //파일을 추가한 input 값

		thumbext = substr(thumbext, ufn_lastIndexOf(thumbext)); //확정자값 가져오기 
		thumbext = ToLower(thumbext); //소문자로 변경

	    if(thumbext != "jpg" && thumbext != "jpeg" && thumbext != "png" &&  thumbext != "gif" &&  thumbext != "bmp"  && thumbext != "pdf")
	    { // 확장자를 확인
			global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		    global.FRM_BOTTOM.st_msg.color = "yellow";       
			global.FRM_BOTTOM.st_msg.value = "이미지 파일(jpg, jpeg, png, gif, bmp, pdf)만 등록 가능합니다.";
			return false;
		}

		/*
		if(ds_file.rowcount < 6)
		{*/
			ds_temp.ClearData();
			ds_temp.addRow();
			ds_temp.setColumn(0, "PJT", ds_master.getColumn(0, "PJT"));
			ds_temp.setColumn(0, "HNO", ds_master.getColumn(0, "HNO"));
			ds_temp.setColumn(0, "GBN", sGbn);
			ds_temp.setColumn(0, "FNAME", fileDialog.FileName);
			ds_temp.setColumn(0, "FSIZE", fileSize);
			ds_temp.setColumn(0, "FPATH", fileDialog.FilePath);



			if(length(ds_temp.getColumn(0, "FNAME")) != 0 && length(ds_temp.getColumn(0, "FPATH")) != 0)
			{
				//fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_i.getColumn(i, "HNO")+ds_list_i.getColumn(i, "CHASU")+"."+gfn_getFileExt(ds_list_i.getColumn(i, "FNAME"));
				fileName = left(GetDate(),8) +"_"+ right(GetDate(),6) +"_"+ G_USER_ID + "_" + ds_temp.getColumn(0, "PJT") + "_" + ds_temp.getColumn(0, "HNO") + sGbn + "."+ gfn_getFileExt(ds_temp.getColumn(0, "FNAME"));

				var arr = tit_fileWrite(httpFile
					, "PS"
					, ds_temp.getColumn(0, "FPATH")
					, ds_temp.getColumn(0, "FNAME")
					, fileName// 실제 db에 저장될 파일 이름
					, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
					, ds_temp.GetColumn(0,"DEL_FNAME"));    // ds_list_i.getColumn(i, "DEL_FNAME") );//삭제할 이전 파일명 
				if ( arr[0] == "S" ) {
					ds_temp.setColumn(0, "FNAME_APP", fileName);
				}
				else if ( arr[0] != "S" )
				{
					gfn_showMsg("CW00035"); 
					return;
				}

				// 파일 추가 저장
				tit_clearActionInfo();	
				tit_addSingleActionInfo("ps11:PS1107001A_I8");
				tit_callService(
					""
					, ""        
					, "ds_temp=ds_temp"
					, ""
					, ""
					, "fn_afterFileAdd"
					, ""
					, ""
					,true  // ""
					);
			}

		/*}
		else
		{
			gfn_openMsgWin("추가할 수 있는 파일 개수를 초과했습니다.\n (최대 6개 저장가능)","","A");
			return;
		}*/

	  //ds_list.setColumn(i, "GBN", cb_gbn.Value);
	}

}

/********************************************************************************
* 기      능   : 파일 삭제
********************************************************************************/
function btn_fileDel_OnClick(obj)
{

	ds_file_o.fireevnet = false;
	ds_file_p.fireevnet = false;
	ds_file_e.fireevnet = false;
	var sGbn   = "";

	if(obj.id == 'btn_fileDel')	// 오버헤드
	{
		if(ds_file_o.rowcount == 0) return;
		ds_file2.copy(ds_file_o);
	}
	else if(obj.id == 'btn_fileDel2')	// 피트
	{
		if(ds_file_p.rowcount == 0) return;
		ds_file2.copy(ds_file_p);
	}
	else // Add3 : 기타(ETC)
	{
		if(ds_file_e.rowcount == 0) return;
		ds_file2.copy(ds_file_e);
	}


	var nChkRow = ds_file2.SearchRow("CHK == '1'");

	if(nChkRow < 0) return;

	for(var i=0; i<ds_file2.rowcount; i++)
	{
		var sFileName = ds_file2.GetColumn(i, "FNAME");
		var sChk      = ds_file2.GetColumn(i, "CHK");

		if(length(sFileName) != 0 && sChk == "1")
		{
			var arr = tit_fileDelete("PS",ds_file2.getColumn(i, "FNAME_APP"), false);
		}
	}

	ds_temp2.clearData();

	// 체크된 파일만 삭제처리
	if(obj.id == 'btn_fileDel')	// 오버헤드
	{
		ds_temp2.Copy(ds_file_o);
		ds_temp2.Filter("CHK==1");
		ds_file2.copyF(ds_temp2);
		ds_temp2.UnFilter();

	}
	else if(obj.id == 'btn_fileDel2')	// 피트
	{
		ds_temp2.Copy(ds_file_p);
		ds_temp2.Filter("CHK==1");
		ds_file2.copyF(ds_temp2);
		ds_temp2.UnFilter();

	}
	else // Add3 : 기타(ETC)
	{
		ds_temp2.Copy(ds_file_e);
		ds_temp2.Filter("CHK==1");
		ds_file2.copyF(ds_temp2);
		ds_temp2.UnFilter();

	}

	ds_file_o.fireevnet = true;
	ds_file_o.fireevnet = true;
	ds_file_e.fireevnet = true;

	// 파일 삭제 저장
	tit_clearActionInfo();	
	tit_addMultiActionInfo("ps11:PS1107001A_D1");
	tit_callService(
		""
		, ""        
		, "ds_file=ds_file2"
		, ""
		, ""
		, "fn_afterFileAdd"
		, ""
		, ""
		,true  // ""
		);
}

/********************************************************************************
* 기      능   : 파일 저장,삭제 후, Callback
********************************************************************************/
function fn_afterFileAdd()
{

	// 저장 후, 파일리스트 재조회
	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps11:PS1107001A_S8");	// 첨부파일 정보
	tit_addSearchActionInfo("ps11:PS1107001A_S9");	// 첨부파일 정보
	tit_addSearchActionInfo("ps11:PS1107001A_S10");	// 첨부파일 정보
	tit_callService(
	 ""
	, ""
	, "ds_cond=ds_master"
	, "ds_file_o=ds_file_o ds_file_p=ds_file_p ds_file_e=ds_file_e"
	, ""
	, "fn_afterFileSelect"
	, ""
	, ""
	, true);



}

