	var sHno            = ds_file_o.GetColumn(nRow, "HNO");
	var sServerDir      =  "PS";
	var sLocalFileName  =  ds_file_o.GetColumn(nRow, "FNAME");
	var sLocalFileDir   =  ds_file_o.GetColumn(nRow, "FPATH");

	var sServerFileName =  ds_file_o.GetColumn(nRow, "FNAME_APP");

	if ( length( sServerFileName) > 0) {
		if( ufn_isImage( sServerFileName )  ) {
				div_pop.img_sitePic.ImageID = tit_getServerDir(sServerDir, sServerFileName);
				//trace(tit_getServerDir(sServerDir, sServerFileName));
				//webBrowser.pageUrl = tit_getServerDir(sServerDir, sServerFileName);
				//WebBrowser.run();
			}
		/*	
