function ufn_lastIndexOf(sParmString)
{
	var j = 0;//.위치 
	var k = 0;// 결과위치 
	var k = 0;//.위치 -1 


	while(true) 
	{ 
		r = pos(sParmString,".",j);

		if (r == -1) 
		{ 
			break; 
		} 

		j=r+1; 

	} 
	k = j; 

	return k;
}

function ufn_isImage(fileNm) {
	var s = toUpperCase(right( fileNm, 4));
	if ( s == ".JPG" || s == ".GIF" || s == ".PNG" || s == ".BMP") {
		return true;
	} else if ( toUpperCase(right( fileNm, 5)) == ".JPEG") {
		return true;
	}

	return false;
}

