/********************************************************************************
* 기      능   : 이미지 더블 클릭 시, 실행 (오버헤드)
********************************************************************************/
function grd_imgList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0)
	{

		if( length(ds_file_o.getColumn(nRow, "FNAME")) > 0) {
		   var sPjtNo = ds_file_o.GetColumn(nRow, "PJT");
		   var sHno   = ds_file_o.GetColumn(nRow, "HNO");
		   // 첨부 파일 처리 
		   fn_createWait("", this);

			var FilePath = "c:\\Temp" ;		
			var FileExec = "c:\\Temp\\" + ds_file_o.getColumn(nRow, "FNAME");

			saveDialog.FileName = ds_file_o.getColumn(nRow, "FNAME");

			var rMsg = tit_fileRead(
										httpFile
										, "PS"  	  // 저장 디렉토리
										, ds_file_o.getColumn(nRow, "FNAME_APP")	// 서버에서 전송받을 이름
										, FilePath    // 로컬 경로
										, ds_file_o.getColumn(nRow, "FNAME")    // 로컬 파일명
									   );

				fn_destroyWait();
				if ( rMsg[0] == "E" ) {
					if ( length(trim(rMsg[1])) > 0 ) {
					  //  ExecShell(FileExec);
						gfn_showAlert("CW10025", rMsg[1]);
					} else {

						gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
					}
				} else {	
					ExecShell(FileExec);		
				}	


			 }
	}
}

