function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	//gfn_showSearchMsg(Edit3);
	gfn_showSearchMsg(div_pop.Edit3);

	if( ds_detail.rowcount <= 0 )
	{
		gfn_initDs(ds_detail, true);
	}

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
   if ( errCode != 0 ) {
		chk = 'X';
		gfn_showMsg("CE00001");

	} else {
		gfn_showMsg("CI00003");		
		SELFLG = "Y";
		fn_query();
	}	

}
/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) 
{
   if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
		SELFLG = "Y";
		fn_query();
	}	

}
/********************************************************************************
* 기      능   : 출력전 저장 Callback  
