function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{


	if(strColumnID=="A_2_1" or strColumnID=="A_2_2" or strColumnID=="A_2_3" or strColumnID=="A_2_4" or
	   strColumnID=="A_2_5" or strColumnID=="A_2_6" or strColumnID=="A_2_7" or strColumnID=="A_2_8")
	{
		   if(length(ds_detail.GetColumn(nRow,"A_2_1")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A_2_2")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_3")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_4")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_5")) == 0 and 
		    length(ds_detail.GetColumn(nRow,"A_2_6")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_7")) == 0 and
		    length(ds_detail.GetColumn(nRow,"A_2_8")) == 0 )
		    {
				ds_detail.fireevent = false;
		    }


	}
	//============================================================================
	if(strColumnID=="B_2_1" or strColumnID=="B_2_2" or strColumnID=="B_2_3" or strColumnID=="B_2_4") 
	{
		    if(length(ds_detail.GetColumn(nRow,"B_2_1")) == 0 and
		       length(ds_detail.GetColumn(nRow,"B_2_2")) == 0 and
		       length(ds_detail.GetColumn(nRow,"B_2_3")) == 0 and 
		       length(ds_detail.GetColumn(nRow,"B_2_4")) == 0 ) 
                {
					ds_detail.fireevent = true;
                }
      }


}

function fn_print() 
{
	tit_clearActionInfo();		
	//tit_addMultiActionInfo("ps11:PS1101003B_U2");		
	//tit_addMultiActionInfo("ps11:PS1109001B_U2");		
	tit_addSelectExActionInfo("ps11:PS1109001B_S3","ps11:PS1109001B_U2", "ps11:PS1109001B_U3");
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterPrint"
		, ""
		, ""
		, true);	
}

function fn_print2() 
{
	ds_master_p.DeleteAll();
	ds_detail_p.DeleteAll();
	ds_master_p.AddRow();
	ds_detail_p.AddRow();

	// 저장할 데이터셋을 복사
	ds_master_p.CopyRow(0,"ds_master",0);
	ds_master_p.SetColumn(0,"HDM",gfn_formatDate(ds_master_p.GetColumn(0,"HDM"),'.'));
	ds_master_p.SetColumn(0,"BJD",gfn_formatDate(ds_master_p.GetColumn(0,"BJD"),'.'));
	ds_master_p.SetColumn(0,"NAP",gfn_formatDate(ds_master_p.GetColumn(0,"NAP"),'.'));

	var colum;
	var row;
	//colum = ds_master.GetColumn(0,"FRAME_CK");
	row   = ds_frame.SearchRow("CODE=='"+colum+"'");
	ds_master_p.SetColumn(0,"FRAME_CK", ds_frame.GetColumn(row,"CODE_NAME"));
//alert(	ds_frame.GetColumn(row,"CODE_NAME")  );

	if (ds_master_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	ds_detail_p.CopyRow(0,"ds_detail",0);

    /*
