	//chk_so4.Value = '1';


	ed_tran_f.value = substr(gfn_getCurrDate(),0,6)+"01";
	ed_tran_t.value = gfn_getLeapLastDay(gfn_getCurrDate());



	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);




	ed_tran_f.SETFOCUS();
	gfn_showMsg("CI00007");

}



